INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2015, '2015年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"藩篱 樊笼 翻天覆地\",\"B\":\"几案 肌理 反唇相讥\",\"C\":\"揭露 露底 风餐露宿\",\"D\":\"结实 结余 结党营私\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.908 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列各组词话中，书写完全正确的一项（ ）"', '"{\"A\":\"温馨 墨许 渺无人烟\",\"B\":\"启蒙 冥想 矫往过正\",\"C\":\"鹅黄 雀跃 闻鸡起舞\",\"D\":\"藐视 扼腕 花拳秀腿\"}"', 'C', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.916 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2015, '2015年体育单招文化考试', '"依次填入下列横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"调试 体貌 功能\",\"B\":\"调适 体貌 功绩\",\"C\":\"调试 体魄 功绩\",\"D\":\"调适 体魄 功能\"}"', 'D', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.922 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2015, '2015年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"在国际市场竞争中，虽说“胳膊拧不过大腿”，但这家小厂却凭借产品更新赢得了发展。\",\"B\":\"“墙倒众人推” ，只要我们团结一致，充分发挥集体的智慧和力量，就一定能战胜困难。\",\"C\":\"割鸡焉用牛刀？对付这样的弱队，我们主力队员不上场也完全能取胜，这个自信应该有。\",\"D\":\"对于这次比赛事故，他的辩解驴唇不对马嘴，偏离事实，自相矛盾，难以服人。\"}"', 'C', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.928 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2015, '2015年体育单招文化考试', '"下列各句中加点成语的使用，不恰当的一项是（ ）"', '"{\"A\":\"毋庸置疑，“四肢发达就是体育”的陈旧观念，还难以在社会上和学校里彻底消除。\",\"B\":\"成功体验与竞争意识相辅相成。安排比赛要考虑学生在体能、技术方面存在的差异。\",\"C\":\"保了传球、他还不断突破，急停跳投，接二连三撕开对方防线，为比分反超立下大功。\",\"D\":\"快到终点时，他的速度越来越快，强弩之末，势如破竹，终于毫无悬念的夺得了冠军。\"}"', 'D', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.934 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列句子中，没有语病的一项是（ ）"', '"{\"A\":\"预计今年电影产业会继续保持高速发展，院线银幕数量的增加，在线选座等预约服务将使电影呈现扩张态势。\",\"B\":\"这家颇负盛名的书店宣布24小时运营，引来读者无数的 “点赞” ，因为它引领了一种热爱阅读的生活方式。\",\"C\":\"近期，国内鲜奶价格不断上涨，然而受到疫情、养殖成本增加等影响，养殖户不但到了被迫卖牛的境地，甚至没有受益。\",\"D\":\"这里自晚唐五代以来就成为中国非常重要的瓷器生产核心，元代初年设计的瓷器，加强了朝廷对瓷器制造的监督和管理。\"}"', 'A', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.938 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"在横线处填入下列句子，顺序最恰当的一项是（ ）"', '"{\"A\":\"①⑤⑥②③④\",\"B\":\"③⑤⑥④②①\",\"C\":\"①⑥⑤③②④\",\"D\":\"③⑥⑤②①④\"}"', 'B', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.942 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列各句中，没有使用反问修辞手法的一项是（ ）"', '"{\"A\":\"没有了商品交换的社会，岂不要退回到自给自足的原始状态了吗？\",\"B\":\"用汉字写的国语，是否来用地方音读呢？不行！那就不是国语了。\",\"C\":\"我们已经长大成人，可是所有的大人不都是从孩童时代走来的吗？\",\"D\":\"哲学家倘若不能从心灵中汲取大部分的快乐，他算什么哲学家呢？\"}"', 'C', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.947 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2015, '2015年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"人们常会认为-----体育么？就是身体活动，是“简单的”体力活动，不需要动脑筋。\",\"B\":\"“唯分数论”观念不变，体育活动仍将是“讲起来次要，做起来次要，忙起来不要”。\",\"C\":\"一竿在手，钓趣无穷。有晨曦，有落日，有斜风，有细雨，有天光云影、鸢飞鸟啼。\",\"D\":\"突然“咔嚓”一声响，断裂的冰块漂移而去……憋闷了一个冬天的大鱼跳出水面。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.953 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是( )"', '"{\"A\":\"唐代诗人李商隐的《锦瑟》，意象朦胧，意境凄迷，情思缠绵，是脍炙人口的佳作。\",\"B\":\"巴金的散文《包身工》，表现了上世纪40年代中国女工在日本纱厂被奴役的黑暗现实。\",\"C\":\"《装在套子里的人》作者是俄国作家契诃夫，别里科夫是这篇小说中的主要人物。\",\"D\":\"《我有一个梦想》是美国黑人民权运动领袖马丁·路德·金在一次集会上的演讲。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.958 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2015, '2015年体育单招文化考试', '"下列词语中加点字的读音，不正确的一项是（ ）"', '"{\"A\":\"衡量（liáng)\",\"B\":\"塑（shù)造\",\"C\":\"时髦（máo)\",\"D\":\"勉强（qiǎng)\"}"', 'B', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.964 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2015, '2015年体育单招文化考试', '"下列对文中词语的解释，不正确的一项是（ ）"', '"{\"A\":\"望眼欲穿：形容盼望急切。\",\"B\":\"价值观：对社会事物持有的总的看法。\",\"C\":\"大举：重大的举动。\",\"D\":\"闷头：不声不响地。\"}"', 'C', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.969 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对文中语句的理解，不正确的一项是（ ）"', '"{\"A\":\"“受这个东西的奴役”，意思是人要为所买的商品付出艰辛劳动。\",\"B\":\"“在家庭财政上戳个窟窿”，意思是使家庭财政超支，出现亏空。\",\"C\":\"“挺吓人的报酬”，意思是意外的收入，令人惶恐不安。\",\"D\":\"“小有表现”，意思是在听课时略微表现出一些才能来。\"}"', 'C', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.974 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"本文三次用“一无所有”一词，对此的分析，正确的一项是（ ）"', '"{\"A\":\"第1段中的“一无所有”，形容现在的年轻人非常贫困，寄予了怜悯和同情。\",\"B\":\"第3段中的“一无所有”，形容“我们结婚”时非常贫困，有自我嘲讽意味。\",\"C\":\"第7段中的“一无所有”，提醒自己不忘过去的贫困，珍惜苦尽甘来的幸福。\",\"D\":\"本文三次用“一无所有”，都引发了如何对待年轻时生活贫困的话题和议论。\"}"', 'D', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.978 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对第4段画线部分的理解，不正确的一项是（ ）"', '"{\"A\":\"作者坚信，只要发奋图强，青年时代甘于贫困一定会有回报的。\",\"B\":\"作者认为，事业有成之后，回首青年时代的艰辛，会有幸福感。\",\"C\":\"“难道就为了100块一天给卖了？”表现出作者对报酬不太满意。\",\"D\":\"“不卖，坚决不卖！”以斩钉截铁的短句表明了作者坚定的态度。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.983 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对本文内容的理解，不正确的一项是（ ）"', '"{\"A\":\"作者讲述自己的经历，说明如何对待青春贫困与事业发展的矛盾，可谓现身说法。\",\"B\":\"作者认为，青春贫困并不可怕，可怕的是为了追逐物质享受而毁灭了青春的前途。\",\"C\":\"“最大限度地把青春留给自己” ，意为要做自己喜欢做的事，要工作，更要休闲娱乐。\",\"D\":\"文章结尾说“对裤子的所有权绝不肯放弃”，照应开头，幽默诙谐，寓意丰富深刻。\"}"', 'C', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.987 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列句中冒号的用法与其他三项不同的一项是（ ）"', '"{\"A\":\"所以，我必须想一想：这双鞋究竟有多重要？\",\"B\":\"我们有自己的理由：不把自己的青春减价出售。\",\"C\":\"还是那个道理：青春不减价拍卖。\",\"D\":\"有颇为庆幸之处：青春有价，没有贱卖。\"}"', 'B', '', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.992 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列语句，最适合做文章标题的一项是（ ）"', '"{\"A\":\"青春代价\",\"B\":\"青春定价\",\"C\":\"青春无价\",\"D\":\"青春有价\"}"', 'C', '', '3', 18, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:06:59.997 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2015, '2015年体育单招文化考试', '"下列句中加点字的读音，不正确的一项是（ ）"', '"{\"A\":\"酿泉为酒 酿：念niàng\",\"B\":\"山肴野萩 肴：念xiáo\",\"C\":\"宴酣之乐 酣：念hān\",\"D\":\"树林阴翳 翳：念yi\"}"', 'D', '', '3', 19, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.001 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"对下列句中加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"云归而岩穴暝 暝：昏暗\",\"B\":\"杂然而前陈者 陈：摆放\",\"C\":\"太守归而宾客从也 从：服从\",\"D\":\"游人去而禽鸟乐也 去：离开\"}"', 'C', '', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.006 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列语句，不属于景物描写的一项是（ ）"', '"{\"A\":\"日出而林霏开\",\"B\":\"野芳发而幽香\",\"C\":\"佳木秀而繁阴\",\"D\":\"四时之景不同\"}"', 'D', '', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.014 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列语句，描写宴会场面的一项是（ ）"', '"{\"A\":\"溪深而鱼肥\",\"B\":\"泉香而酒洌\",\"C\":\"射者中，弈者胜\",\"D\":\"人影散乱\"}"', 'C', '', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.021 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"对“负者歌于途，行者休于树”的翻译，正确的一项是（ ）"', '"{\"A\":\"自负的人在路上唱歌，僧人在树下休息。\",\"B\":\"自负的人在路上唱歌，走路的人在树下休息。\",\"C\":\"背着东西的人在路上唱歌，僧人在树下休息，\",\"D\":\"背着东西的人在路上唱歌，走路的人在树下休息。\"}"', 'D', '', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.025 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对于所选文段内容的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"第一段，先写山中早晚光线的明暗变化，再写四季景物的变换，突出了赏景之乐。\",\"B\":\"第二段，写滁人游山，太守宴饮，突出了宾客喧哗、无的放矢、不醉不休的情景。\",\"C\":\"第三段，写游山晚归，突出了太守与民同乐的自得情怀，以及传诸文字的自豪感。\"}"', 'B', '', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.030 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2016, '2016年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"与其 参与 与日俱增\",\"B\":\"奢靡 靡费 望风披靡\",\"C\":\"勉强 强迫 强词夺理\",\"D\":\"空闲 空虚 空中楼阁\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.036 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2016, '2016年体育单招文化考试', '"下列各组词语中，书写完全正确的一项是（ ）"', '"{\"A\":\"伎两 殊不知 删删来迟\",\"B\":\"期待 全天候 入不敷出\",\"C\":\"寻觅 流览器 按步就班\",\"D\":\"偶像 坐谈会 关怀倍至\"}"', 'C', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.041 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2016, '2016年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"调动 接合 从而\",\"B\":\"发动 结合 从而\",\"C\":\"调动 结合 因而\",\"D\":\"发动 接合 因而\"}"', 'D', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.046 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2016, '2016年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ )"', '"{\"A\":\"作为曾经的世界冠军，王教练坚信自己的弟子能青出于蓝而胜于蓝。\",\"B\":\"如果把自己获胜的希望寄托在对手的松懈上，必然是竹篮打水一场空。\",\"C\":\"他反应迅速，听风就是雨，教练一个手势，他立刻心领神会。\",\"D\":\"是骡子是马，拉出来遛遛，对嘴把式而言，这应该是最有效的检验。\"}"', 'B', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.053 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2016, '2016年体育单招文化考试', '"下列各句中加点成语的使用，恰当的一项是（ )"', '"{\"A\":\"这篇有关比赛的通讯虽然写得相当不错，但由于报道不及时，已是面目全非了。\",\"B\":\"兄弟队频繁地发起肆无忌惮的挑战，这是我们队成员必须面对的严峻事实。\",\"C\":\"今天李义所反映的情况与教练组的观察完全一致，不容置喙，你应该认真面对。\",\"D\":\"很显然，他这一次投出去的标枪歪的太离谱，连裁判都哑然失笑了。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.059 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列句子中，有语病的一项是（ )"', '"{\"A\":\"能不能参加这一次的国家队集训，还要看他自己的训练效果够不够好。\",\"B\":\"教练反复交代要就比赛时间、地点及可能出现的问题做好相应的准备。\",\"C\":\"他沉痛地非常宣布，此次外出决赛计划，又因为安全因素的影响而搁浅。\",\"D\":\"没有人会在这样困难的时刻，再提出一些让训练难以为继的要求。\"}"', 'D', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.065 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"在横线处填上下列句子和短语，顺序最恰当的一项是（ )"', '"{\"A\":\"③①⑤⑥④②\",\"B\":\"③①④②⑤⑥\",\"C\":\"④②⑤⑥③①\",\"D\":\"④②③①⑤⑥\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.069 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下面各句中，没有使用修辞手法的一项（ ）"', '"{\"A\":\"三月的春风，拂动了那湖畔羞涩的杨柳。\",\"B\":\"近看那缀满草尖的水珠，仿佛是数不清的金刚钻。\",\"C\":\"海湾沿岸的灯光，远看去就像蜿蜒在天边的星星。\",\"D\":\"态度端正是实现这次训练目标的关键。\"}"', 'A', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.074 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2016, '2016年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"体校斜对面二三十米处有一家小饭馆，学员们经常在那里招待家人。\",\"B\":\"大家都很关心你是选择继续服役提高成绩？还是离开运动队另谋发展？\",\"C\":\"刚刚结束比赛的魏翔一回到运动队，就接到爸爸“有空去看看奶奶”的口信。\",\"D\":\"我们坚持认为：只有真正全身心地投入训练，才能收获满意的成绩。\"}"', 'B', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.082 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是（ ）"', '"{\"A\":\"《论语》主要记录了孔子及其弟子的言行。\",\"B\":\"《诗经》是我国第一部诗歌总集。\",\"C\":\"《聊斋志异》的作者是明代文学家蒲松龄。\",\"D\":\"《高老头》的作者是法国作家巴尔扎克。\"}"', 'D', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.088 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2016, '2016年体育单招文化考试', '"下列词语中字的读音，不正确的一项是（ ）"', '"{\"A\":\"含蓄（xù)\",\"B\":\"宝藏（cáng)\",\"C\":\"倾慕（qīng)\",\"D\":\"贪婪（lán)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.093 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2016, '2016年体育单招文化考试', '"下列词语在文中意思的解释，不正确的一项是（ )"', '"{\"A\":\"震慑：震动恐惧。\",\"B\":\"无可厚非：不可过分指责。\",\"C\":\"盘桓：走来走去。\",\"D\":\"肃然起敬：内心表现出恭敬。\"}"', 'A', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.099 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对第1段画线句子的理解和分析，不正确的一项是（ )"', '"{\"A\":\"“拦截”突出了海水中石头对“我”的巨大吸引力。\",\"B\":\"这句话诙谐的写出了作者未能极目远眺时的失望。\",\"C\":\"这句话包含着无名海滩带给作者内心的某种惊喜。\",\"D\":\"这句话写出了作者当时的心境，并自然引出下文。\"}"', 'D', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.104 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对第2段中一些词句含意的理解，不正确的一项是（ )"', '"{\"A\":\"从“有的…有的…”这些语句中，可见海水中石头色彩的多样性。\",\"B\":\"“气度不凡地占据了海滩” ，写出了水中的石头带给作者的心理冲击。\",\"C\":\"“犹如贪婪的盗墓者” ，写出了作者对于自己破坏环境的行为的自责。\",\"D\":\"从文意来看，“那些海水中的石头”中的“那些海水中的”是不可缺少的。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.111 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对第3、4、5段有关内容的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"“又一批宝贝”带给作者的感受迥异于那些被“首批中选”的石头。\",\"B\":\"“不再美丽”“灰头灰脸”“死亡”是作者对离开大海的石头的感受。\",\"C\":\"作者前后感受不同的原因，主要就在于这些石头所处的时间不同。\",\"D\":\"作者在自己的莽撞中发现，有时候喜爱也会给失误造成“伤害”。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.116 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"文中提到科学家法布尔，下列作品属于法布尔作品的一项是（ )"', '"{\"A\":\"《物种起源》\",\"B\":\"《昆虫记》\",\"C\":\"《海底两万里》\",\"D\":\"《自然史》\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.127 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对文章的理解和分析，不正确的一项是（ )"', '"{\"A\":\"文章借石头“生”与“死”的对比，突出了“我”认识上的转变。\",\"B\":\"文章批评了那种因为爱或喜欢就想占有的心理习惯及行为方式。\",\"C\":\"让园子里的石头像那海中的石头一样存在，是法布尔建立荒石园的原因。\",\"D\":\"作者在文中也委婉提出，人类应该尊重事物的自然存在状态。\"}"', 'A', '', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.132 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是（ ）"', '"{\"A\":\"海边荒石\",\"B\":\"那海滩的石头\",\"C\":\"海边石头\",\"D\":\"我的石头记忆\"}"', 'B', '', '3', 18, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.137 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2016, '2016年体育单招文化考试', '"下列各句中加点字的读音，不正确的一项是（ )"', '"{\"A\":\"辍耕之陇上 辍：chuò\",\"B\":\"燕雀安知鸿鹄之志哉 鹄：hú\",\"C\":\"道不通，度已失期 度：duó\",\"D\":\"今诚以吾众诈自称公子扶苏 诈：zuò\"}"', 'C', '', '3', 19, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.141 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列各句中，加点的字是通假字的一项是（ ）"', '"{\"A\":\"尝与人佣耕\",\"B\":\"九百人屯大泽乡\",\"C\":\"扶苏以数谏故\",\"D\":\"为天下唱，宜多应者\"}"', 'D', '', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.145 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列句中加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"苟富贵，无相忘 苟：如果。\",\"B\":\"会天大雨，道不通 会：适逢。\",\"C\":\"爱士卒，楚人怜之 怜：爱戴。\",\"D\":\"或以为死，或以为亡 亡：死亡。\"}"', 'D', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.695 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列句子中，是判断句的一项是（ )"', '"{\"A\":\"陈胜者，阳城人也。\",\"B\":\"等死，死国可乎？\",\"C\":\"上使外将兵。\",\"D\":\"百份多闻其贤，未知其死也。\"}"', 'A', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.706 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列文句的翻译，不正确的一项是（ )"', '"{\"A\":\"若为佣耕，何富贵也？ ——你是一个替人耕田的人，怎么会富贵起来呢？\",\"B\":\"发闾左适戍渔阳。 ——征发贫苦人民去驻守渔阳。\",\"C\":\"天下苦秦久矣。 ——天下百姓生活艰苦，秦统治者知道这一点已很久了。\",\"D\":\"吴广以为然。 ——吴广认为（这个见解）很正确。\"}"', 'C', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.717 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对所选文段内容的理解和分析，不正确的一项是（ )"', '"{\"A\":\"第一段主要介绍陈胜年轻时的情况，“尝与人佣耕”显示了他当时的境况。\",\"B\":\"“燕雀安知鸿鹄之志” 表明了陈胜、吴广两人年轻时就有远大的抱负。\",\"C\":\"陈胜、吴广起义的地点是 “大泽乡” ， “失期，法皆斩” 是起义的导火线。\",\"D\":\"“诈自称公子扶苏、项燕” 的策略，显示出陈胜在决策时的过人胆识。\"}"', 'A', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.724 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2017, '2017年体育单招文化考试', '"下列各组词语中加粗的字，读音全都相同的一项是（ ）"', '"{\"A\":\"埋伏 埋怨 埋头苦干\",\"B\":\"着落 落魄 丢三落四\",\"C\":\"折叠 折扣 令人折服\",\"D\":\"宿命 星宿 体坛宿将\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.150 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列各组词语中，书写完全正确的一项是（ ）"', '"{\"A\":\"座落 虚荣心 目不交捷\",\"B\":\"漫骂 随大流 义气用事\",\"C\":\"融恰 算总账 直言不诲\",\"D\":\"蕴藏 烂摊子 辗转反侧\"}"', 'B', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.155 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下面文段中加粗的词语，能够用括号中词语替代的一项是（ ）"', '"{\"A\":\"一旦（如果）\",\"B\":\"就（也）\",\"C\":\"就（才）\",\"D\":\"从而（所以）\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.160 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2017, '2017年体育单招文化考试', '"下列各句中加粗熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"拜仁慕尼黑足球队来访，北京球迷， 近水楼台先得月 有机会到现场看比赛。\",\"B\":\"这两支球队曾有20次比赛记录，结果是两队各赢10场，真可谓 旗鼓相当 。\",\"C\":\"针对他的新年微博秀，一些网友认为他应该专心训练，而不是急着 唱高调 。\",\"D\":\"他在省队训练的时间不长，有人就认为他在那里不过是一个 泡蘑菇 的角色。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.166 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2017, '2017年体育单招文化考试', '"下列各句中加粗成语的使用，恰当的一项是（ )"', '"{\"A\":\"假如每个人都能 见贤思齐 ，以人之长补己之短，那么我们每天都会有进步。\",\"B\":\"不管他自己怎么认为，对于职业联赛而言，他都只是一个 不名一文 的小人物。\",\"C\":\"遵照相关法律和要求，各行各业中 上行下效 ，社会风气就会逐步好转起来。\",\"D\":\"联赛环境得以改善，地方政府也 推波助澜 ，成就了这一次主场的巨大胜利。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.171 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列句子中，有语病的一项是（ )"', '"{\"A\":\"到底需要不需要重新比赛，取决于仲裁机构最后做出什么样的裁决。\",\"B\":\"他一声不吭，赶紧递过去一块毛巾，心里暗暗期待队友能够长期坚持下去。\",\"C\":\"对运动员而言，汗水、比赛服以及比赛场地，全都是值得回忆的美好事物。\",\"D\":\"从教练组出来时，我们看得出来，他已经摆脱完全从前的那种精神面貌了。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.175 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ )"', '"{\"A\":\"④③②⑤①\",\"B\":\"③①④⑤②\",\"C\":\"④②③①⑤\",\"D\":\"③⑤④①②\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.180 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列各句中，没有使用修辞手法的一项是（ )"', '"{\"A\":\"谁都明白，教练就是教练，绝对不能搞得不像个教练。\",\"B\":\"白发三千丈，缘愁似个长。不知明镜里，何处得秋霜。\",\"C\":\"大家正犹豫，就看见有一个红领巾跑上前去扶起了她。\",\"D\":\"发令枪刚一响，各位选手就像离弦的箭一样冲了出去。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.185 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2017, '2017年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ )"', '"{\"A\":\"小时候我就在村里的小学上学，学校离家不过二三里地，还是很方便的。\",\"B\":\"我们面对的问题是，有些运动员不想付出努力，整天就等着天上掉馅饼？\",\"C\":\"离开训练队很久了，但是“要做就做出个样子来”的声音仿佛还在耳边。\",\"D\":\"这一次比赛我们又大获全胜：兵乓球各项目的冠军再一次为我们包揽了。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.189 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列有关作家作品的表述，不正确的是（ ）"', '"{\"A\":\"《爱莲说》的作者是周敦颐。\",\"B\":\"《三国演义》的作者是罗贯中。\",\"C\":\"《平凡的世界》的作者是路遥。\",\"D\":\"《项链》的作者是法国福楼拜。\"}"', 'B', '成就大事的人，大都有一种“坐忘”的功夫。《庄子》里讲了许多“凝神”的故事，“佝偻承蜩”就是其中之一。故事中写的是一个身材佝偻的老人，用竹竿捕蝉，就像用手拾取一枚落叶一样轻而易举。为什么能够这样轻松呢？老人说我是经过刻苦训练的。我在竹竿上叠加一些小泥丸，先是叠加两颗，接着三颗，最后五颗。当伸出竹竿捕蝉时，竹竿头上的五颗泥丸一颗也不掉下来，说明我的稳定性已经绝佳。到那时候，再用竹竿捕蝉，就好像是用手去捡一样，万无一失。而且，我的身子站定在那，就像没有知觉的断木桩子；我举着的手臂，就像枯树枝；即使天地很大，物类繁盛，可我只知道有蝉翼在眼前。我不回头不侧身，不因万物而改变对蝉翼的注意，还有什么蝉捕不到？庄子用这个故事，告诉了我们“用志不分，乃凝于神”的道理。 “坐忘”是一种崇高的人生境界。人生苦短，应该把主要精力用在有价值的事情上，不要受一些外在事物的干扰。不要在外在的名利上过于计较，做一件事，只要觉得它于世道人心有价值，就应该专心去做，至于结果如何，自己能不能从这件事上得到什么好处，那都不是自己该考量的。有了这样坦然的心态，为人就不会患得患失，做事就可以善始善终，倘若真能够百折不回，也就有可能做出一些大事来。 “坐忘”的精神，就是要执着一念，滴水穿石。光阴不是用来消磨的，是用来开花结果的。“坐忘”的人，就是用心浇灌光阴这棵美丽的树，等到树上开花结果。如果无视光阴这颗美丽的树，不去经营它，任凭它枯萎掉，那样的人生，该是多么失败！ 11. 下列词语中加粗的字读音，不正确的一项是（ ）', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.194 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2017, '2017年体育单招文化考试', '"下列词语中加粗的字读音，不正确的一项是（ ）"', '"{\"A\":\"浮躁（zào)\",\"B\":\"干扰（rǎo)\",\"C\":\"执着（zháo)\",\"D\":\"枯萎（wěi)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.200 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列词语中加粗字的解释，不正确的一项是（ ）"', '"{\"A\":\"心无旁骛（追求）\",\"B\":\"掷去之（抛掷）\",\"C\":\"佝偻承蜩（拾取）\",\"D\":\"凝于神（盯着）\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.205 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列词语在文中的意思，解释不正确的一项是（ ）"', '"{\"A\":\"魂不守舍：指做事情时精神不能集中。\",\"B\":\"判若云泥：形容高低差异很难去判断。\",\"C\":\"轻而易举：形容做事情很容易就成功。\",\"D\":\"患得患失：一般指为人处世顾虑重重。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.211 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('reading', 2017, '2017年体育单招文化考试', '"阅读文章第三段，下列理解或分析不正确的一项是（ )"', '"{\"A\":\"这一段在讲管宁和华歆的交往时，提到了他们生活中的两个小故事。\",\"B\":\"从本文介绍看，“金子”和“车马”对管宁和华歆的影响截然不同。\",\"C\":\"管宁选择与华歆割席绝交，是因为他不愿意接受华歆的财物和身份。\",\"D\":\"管宁和华歆的区别，其实在于能不能够专心对待自己正在做的事情。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.218 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('reading', 2017, '2017年体育单招文化考试', '"阅读文章第四段，下列理解或分析正确的一项是（ )"', '"{\"A\":\"文章这一段写的是成就大事的人，与第三段写管宁华歆的生活小事不相同。\",\"B\":\"佝偻老人捕蝉可以万无一失，原因就在于他用刻苦训练弥补了身体的缺陷。\",\"C\":\"文章写佝偻老人的捕蝉训练过程，表明技艺进步永远都没有止境这个道理。\",\"D\":\"佝偻老人的成功，既在于有技术，也在于他有一种忘我的精神境界与追求。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.223 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"文中提到《庄子》和《世说新语》，相关常识表述有误的一项是（ )"', '"{\"A\":\"文章借《庄子》的“坐忘”起笔，明确自己对“坐忘”的不同看法。\",\"B\":\"文章对比解释“坐忘”“坐驰”，并引《世说新语》中的故事来说明。\",\"C\":\"文章用“佝偻承蜩”老人的超凡技能，来突出“坐忘”的崇高境界。\",\"D\":\"文章希望大家都能够有“坐忘”这样的境界，去做一些有价值的事。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.228 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列词语中，最适合做文章标题的一项是（ )"', '"{\"A\":\"“坐忘”小议\",\"B\":\"坐忘\",\"C\":\"“坐忘”与“坐驰”\",\"D\":\"凝神\"}"', 'C', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.731 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2017, '2017年体育单招文化考试', '"下列各句中加粗字的读音，不正确的一项是（ )"', '"{\"A\":\"初级狭，才通人 狭：jiá\",\"B\":\"土地平旷，屋舍俨然 俨：yǎn\",\"C\":\"便要还家，设酒杀鸡作食。 要：yāo\",\"D\":\"率妻子邑人来此绝境。 率：shuài\"}"', 'A', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.738 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列句子加粗的词的解释，不正确的一项是（ ）"', '"{\"A\":\"复前行，欲穷其林 穷：走到头\",\"B\":\"有良田美池桑竹之属 属：一类\",\"C\":\"余人各复延至其家 延：拖延\",\"D\":\"便扶向路，处处志之 志：做标记\"}"', 'D', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.745 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列句中加粗的词语，与现代汉语意思不同的一项是（ ）"', '"{\"A\":\"缘溪行，忘路之远近\",\"B\":\"仿佛若有光\",\"C\":\"阡陌交通，鸡犬相闻\",\"D\":\"乃不知有汉，无论魏晋\"}"', 'C', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.752 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2017, '2017年体育单招文化考试', '"下列选项中，全都是成语的一项是（ ）"', '"{\"A\":\"①②③④\",\"B\":\"①②③⑤\",\"C\":\"①②④⑤\",\"D\":\"②③④⑤\"}"', 'C', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.760 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列文句的翻译，不正确的一项是（ ）"', '"{\"A\":\"渔人甚异之－渔人，觉得非常奇异\",\"B\":\"便舍船，从口入－于是停下船，从山上的小口进入\",\"C\":\"不复出焉，遂与外人间隔－－不再从这里出去就和人间分隔开来，\",\"D\":\"不足为外人道也－不值得向外面的人说（在这里的情况）\"}"', 'A', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.768 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列对所选文段内容的理解和分析不正确的一项（ ）"', '"{\"A\":\"桃花源的发现得益于一个渔人一次偶然的发现和探寻。\",\"B\":\"桃花源的环境非常美好，人们在这里过着幸福的生活。\",\"C\":\"桃花源人以耕作为业，对还有渔人这样的人感到吃惊。\",\"D\":\"桃花源中的居民并不希望自己平静的生活被打扰。\"}"', 'D', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.775 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2018, '2018年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"假话 假设 假公济私\",\"B\":\"计划 划船 划清界限\",\"C\":\"凶恶 恶心 深恶痛绝\",\"D\":\"内蒙 蒙骗 蒙头转向\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.233 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"下列各组词语中，书写完全正确的一项是（ ）"', '"{\"A\":\"等候 坐上客 鞠躬尽瘁\",\"B\":\"奢华 牛角尖 轻重缓急\",\"C\":\"少许 历程碑 居安思危\",\"D\":\"稍息 居留权 礼上往来\"}"', 'B', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.239 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"表现 然而 即使\",\"B\":\"表现 那么 如果\",\"C\":\"出现 但是 如果\",\"D\":\"出现 不过 即使\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.243 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2018, '2018年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"她边走边笑道：“你贵人多忘事，哪里还记得我们这些小百姓呢。”\",\"B\":\"果然是人靠衣裳马靠鞍，她这么一打扮，简直跟换了个人似的。\",\"C\":\"这件事八字没一撇呢，你先别着急啊，咱们有的是时间来应对。\",\"D\":\"他们俩不打不相识，自此变成了仇人，互相争斗怨恨了一辈子。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.248 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2018, '2018年体育单招文化考试', '"下列各句中加点成语的使用，恰当的一项是（ ）"', '"{\"A\":\"五月的黄山是最美的，风景如画，气候宜人，前来旅游的人不绝如缕。\",\"B\":\"谈起黄梅戏，这个年仅九岁的孩子竟然说得头头是道，让人惊叹不已。\",\"C\":\"对孩子进行爱国教育不仅是学校的工作，更是每个家长任劳任怨的责任。\",\"D\":\"这次物理竞赛，以前成绩平平的小明竟然得了一等奖，真让人另眼相看。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.254 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列句子中，没有语病的一项是（ ）"', '"{\"A\":\"不管天气极其炎热，环卫工人为了大家有个干净舒适的环境，仍然坚持工作。\",\"B\":\"参观了这次科技展以后，使我们了解到了我们国家科技发展取得的巨大成就。\",\"C\":\"我们要努力构建体育宣传新格局，为体育事业改革发展营造良好的舆论氛围。\",\"D\":\"兴办孔子学院让世界不仅更加了解中国，而且激发了外国友人学汉语的热情。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.259 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ ）"', '"{\"A\":\"③①⑤⑥④②\",\"B\":\"③①④②⑤⑥\",\"C\":\"④②⑤⑥③①\",\"D\":\"④②③①⑤⑥\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.264 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列各句中，没有使用修辞手法的一项是（ ）"', '"{\"A\":\"小屋的出现，点破了山的寂寞，增加了风景的内容。\",\"B\":\"雌凤蝶背上，是两对绣着黄色花纹的黑绒似的翅膀。\",\"C\":\"她每晚在楼上挂一盏明灯，为前来相会的爱人引路。\",\"D\":\"月光如流水一般，静静的泻在这一片叶子和花上。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.271 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2018, '2018年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"亚洲地跨寒、温、热三带，又因各地地形和距离海洋远近不同，气候复杂多样。\",\"B\":\"张华考上了北京大学，在化学系学习；李萍考进中等技术学校，读机械制造专业。\",\"C\":\"现代画家徐悲鸿笔下的马，正如有的评论家说的那样，“神形兼备，充满生机”。\",\"D\":\"读完课文，他站起来走到老师面前问道：“老师，“有条不紊”的紊是什么意思？”。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.278 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列有关作家作品的表述，不正确的是（ ）"', '"{\"A\":\"《兰亭序》是唐代著名书法家王羲之所作。\",\"B\":\"《孟子》记载了战国思时期想家孟轲的言行。\",\"C\":\"《荷塘月色》是朱自清写的一篇抒情性散文。\",\"D\":\"《巴黎圣母院》是法国作家雨果的长篇小说。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.283 +00:00');
