INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"下列对文中词语的解释，不正确的一项是（ )"', '"{\"A\":\"瞥见：长久注视\",\"B\":\"赫然：引人关注\",\"C\":\"交叠：交错重合\",\"D\":\"朴素：简单朴素\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.293 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对第1段和第2段有关内容的理解和分析，不正确的一项是（ )"', '"{\"A\":\"作者感到震惊的原因是他没想到缝纫机与三十多年前一样新。\",\"B\":\"母亲专注车裤子的神情让作者回忆起了童年的点点滴滴。\",\"C\":\"第2段起作者开始回忆童年，并借此展现了母亲的勤劳。\",\"D\":\"作者通过对写功课这一件事的描述，写出了贫困生活的乐趣。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.298 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对第2段和第3段中一些句子含意的理解，不正确的一项是（ )"', '"{\"A\":\"“门外就是爸爸种的一大片枣子园”自然带出下文给母亲摘枣子的内容。\",\"B\":\"“她做的是把面粉袋、肥料袋车成简单的服装”，表现了母亲的心灵手巧。\",\"C\":\"“疤痕，如今还清晰可见”写出了作者对哥哥仍然颇有微词。\",\"D\":\"“心里却是无所畏惧的”，表现出母亲在生活中的坚强感染到了作者。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.303 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对第4段有关内容的理解和分析，不正确的一项是（ )"', '"{\"A\":\"每天早上，太阳出来以后，母亲就开始准备工作了。\",\"B\":\"光线打在母亲侧脸上显现出的色彩与线条，让作者的印象尤为深刻。\",\"C\":\"家中的孩子们对母亲的辛劳都看在眼中，并争相摘枣子敬献给母亲。\",\"D\":\"母亲用自己的辛勤劳作让孩子们从贫苦的生活中感受到无边的幸福。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.309 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"文中第2段画线处的词语，填写恰当的一项是（ )"', '"{\"A\":\"由于 因此\",\"B\":\"虽然 也不至于\",\"C\":\"虽然 尚未\",\"D\":\"即使 但不曾有\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.315 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对文章的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"文章借助缝纫机这一实物，由现在推及过去，表现了母亲的辛勤劳作。\",\"B\":\"文章的语言简单、质朴，辞藻虽不华美，情感却令人动容。\",\"C\":\"作者运用细节描写来追忆童年生活，凸显了“母爱”这一主题。\",\"D\":\"文章结尾用寥寥数语表现出作者对母亲衰老，时光易逝的感慨。\"}"', 'D', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.780 +00:00');
