INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列句中加点词的解释，不正确的是（ ）"', '"{\"A\":\"晋军函陵 军：驻军\",\"B\":\"犹不如人 犹：尚且\",\"C\":\"郑既知亡矣 既：已经\",\"D\":\"又欲肆其西封 肆：放肆\"}"', 'B', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.801 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列句中，“以”字的意义不同于其他三项的是（ ）"', '"{\"A\":\"以其无礼于晋\",\"B\":\"越国以鄙远\",\"C\":\"焉用亡郑以陪邻\",\"D\":\"阙秦以利晋，唯君图之\"}"', 'D', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.806 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列文句的翻译，不正确的一项是（ ）"', '"{\"A\":\"贰于楚也 ——从属于晋的同时又从属于楚\",\"B\":\"无能为也已 ——不能干什么了\",\"C\":\"尝与晋君赐矣 ——曾经被晋国国君赏赐\",\"D\":\"将焉取之 ——将从哪里取得他所贪求的土地呢\"}"', 'D', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.812 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对所选文段内容的理解和分析，不正确的一项是（"', '"{\"A\":\"第一段交代了秦晋两国攻打郑国的原因。\",\"B\":\"在烛之武壮年时，郑伯就曾请他参政议政。\",\"C\":\"晋国曾经答应给秦国土地，却没有兑现。\",\"D\":\"最终，秦国单方面与郑国结盟，撤军而回。\"}"', 'C', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.820 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对文中画波浪线部分的断句，正确的一项是（ ）"', '"{\"A\":\"吾不能早用／子今急而求子／是寡人之过也／然郑亡／子亦有不利焉。\",\"B\":\"吾不能早用子／今急而求子／是寡人之过也／然郑亡子／亦有不利焉。\",\"C\":\"吾不能早用／子今急而求子／是寡人之过也然郑亡／子亦有不利焉。\",\"D\":\"吾不能早用子／今急而求子／是寡人之过也／然郑亡／子亦有不利焉。\"}"', 'C', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.826 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2019, '2019年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全都相同的一项是（ ） [cite: 572]"', '"{\"A\":\"卷曲 席卷 开卷有益\",\"B\":\"数秒 历数 数九寒天\",\"C\":\"度量 弧度 审时度势\",\"D\":\"难堪 责难 难能可贵\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.320 +00:00');
