INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2019, '2019年体育单招文化考试', '"[cite_start]依次填入下面一段文字横线处的词语，最恰当的一项是（ ) [cite: 582]"', '"{\"A\":\"传达 表扬 反而\",\"B\":\"传播 表彰 反而\",\"C\":\"传播 表彰 进而\",\"D\":\"传达 表扬 进而\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.329 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2019, '2019年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用，不正确的一项是（ ) [cite: 588]"', '"{\"A\":\"彼一时，此一时，现在训练条件好了，我们应更珍惜每一次训练，提高成绩。\",\"B\":\"如果训练出了问题，我们一定要抓住机会，头痛医头，脚痛医脚，及时解决。\",\"C\":\"他高考成绩没有预期的好，这让他在填报志愿时高不成，低不就，左右为难。\",\"D\":\"摩托车赛手在比赛中摔倒，虽然有惊无险，但当时还是让观众为他捏一把汗。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.333 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2019, '2019年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ) [cite: 593]"', '"{\"A\":\"如果在训练时不刻苦不认真，心猿意马，那么在正式比赛中就很难取得好的成绩。\",\"B\":\"有关部门统筹了各方面的调研实践，集思广益，最终制定了这项运动的指导纲要。\",\"C\":\"现阶段，体育产业进入了高速发展时期，群众对体育产品的接受度可谓基器尘上。\",\"D\":\"竞技体育、学校体育与群众体育，三者应该相互协调、相辅相成，不能有所偏颇。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.338 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 598]"', '"{\"A\":\"赛会申办能否成功，主要取决于申办城市基础设施的建设是不是完善。\",\"B\":\"作为鄂温克族最具特色的传统体育项目，“抢枢”运动已有千年历史。\",\"C\":\"完善而又强有力的冰雪运动管理体制是冰雪运动健康发展的有力保证。\",\"D\":\"从长远看，全国青少年校园足球活动是一个需要常抓不懈，不能放松。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.341 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]在横线处填入下列句子或短语，顺序最恰当的一项是（ ) [cite: 603]"', '"{\"A\":\"①②⑤③⑥④\",\"B\":\"②①③④⑥⑤\",\"C\":\"②①⑤④③⑥\",\"D\":\"①②④③⑤⑥\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.346 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]下面各句中使用的修辞手法，与其他三项不同的一项是（ ) [cite: 613]"', '"{\"A\":\"这株高大的冬樱花，一到新年，便盛放如一蓬温柔的火焰。\",\"B\":\"河水一段儿清泠泠，一段儿喧哗不已，在向我们大声吵嚷。\",\"C\":\"到了黄昏时分，麦草已经像小山一样地堆在静寂的麦场上。\",\"D\":\"初秋，蒲公英的花絮飞满天空，就像一朵朵洁白的降落伞。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.352 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2019, '2019年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ) [cite: 618]"', '"{\"A\":\"“南国书城” 《天一阁》，是我国现存最早的一座私家藏书楼。\",\"B\":\"一个骑着摩托车的年轻人在我身边停下，问我：你要找哪家？\",\"C\":\"两三丈外，只见一片烟云－仍遮抹一切，只不过是雾样罢了。\",\"D\":\"推开窗。雨，密密匝匝地在树上响作一团，一滴滴敲响树叶。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.355 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]下列有关作家作品的表述，不正确的一项是（ ) [cite: 623]"', '"{\"A\":\"《离骚》是战国时代楚国屈原的抒情诗。\",\"B\":\"《西厢记》的作者是明代戏剧家王实甫。\",\"C\":\"《边城》是中国现代作家沈从文的小说。\",\"D\":\"《老人与海》的作者是美国作家海明威。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.359 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2019, '2019年体育单招文化考试', '"下列词语中加点的字的读音，不正确的一项是（ )"', '"{\"A\":\"承载（zài)\",\"B\":\"储存（zhù)\",\"C\":\"摇曳（yè)\",\"D\":\"掺杂（chān)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.363 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2019, '2019年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ )"', '"{\"A\":\"融合：合为一体。\",\"B\":\"货真价实：货不假，价实在。\",\"C\":\"肆意：竭尽全力。\",\"D\":\"小心翼翼：举动谨慎，不敢疏忽\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.367 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对第1段三个“忍不住”语意指向的理解不正确的一项是（ ）"', '"{\"A\":\"第一个“忍不住”表现了超市里粮食对作者的吸引力。\",\"B\":\"第二个“忍不住”表现了作者亲近粮食的渴望和行为。\",\"C\":\"第三个“忍不住”表现了作者面对粮食产生无尽感慨。\",\"D\":\"三个“忍不住”都表现出作者对人们漠视粮食的伤感。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.371 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对第2段中画线句子的理解和分析，正确的一项是（ )"', '"{\"A\":\"“花花绿绿”，是指超市里面那些吸引消费者的好看而又抢眼的商品。\",\"B\":\"“这个过程”，是指粮食从生产、加工、运输到进入超市的整个过程。\",\"C\":\"这两句表明，人们购物匆匆，没有时间辨析比较粮食的品质与价格。\",\"D\":\"这两句表明，超市信誉较好，粮食价格又便宜，不用挑选就可以买。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.375 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对文中有关内容的理解和分析，正确的一项是（ )"', '"{\"A\":\"“装作很饥饿的样子”，是因为“我”要给孩子做出榜样，让他们热爱粮食。\",\"B\":\"“只吃过几次就失去了资格”，言外之意是家里边又有了更小的弟弟或妹妹。\",\"C\":\"“那炊烟里总是带着粮食的香气”，写出作者在那个饥饿年代对米饭的渴望。\",\"D\":\"母亲“挑几个好看的”地瓜给我们当饭吃，是为了让我们忘记地瓜是猪食。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.379 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对粮食变味的原因的理解，不正确的一项是（ )"', '"{\"A\":\"现在人们生活水平提高了，品尝到的美味多了。\",\"B\":\"现在人们生活节奏加快了，对事物麻木不仁了。\",\"C\":\"现在的粮食缺少自然洗礼、亲人的劳动和情感。\",\"D\":\"现在的粮食从超市里买来，商品化气息太浓厚。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.383 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对文章的理解和分析，不正确的一项是 ( )"', '"{\"B\":\"文章从今昔对比的角度分析问题，因而更加突显了反思精神和失落情怀。\",\"C\":\"文章在议论、抒情中穿插叙述、描写，使得议论和抒情都显得有的放矢。\",\"D\":\"文章的首尾两段遥相呼应，都从珍惜的角度融入思考，从而深化了主题。\"}"', 'D', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.832 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是 ( )"', '"{\"A\":\"粮食的滋味\",\"B\":\"粮食的味道\",\"C\":\"粮食的气息\",\"D\":\"粮食的韵律\"}"', 'C', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.837 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2019, '2019年体育单招文化考试', '"下列句子中加点的字的读音，完全正确的一项是（ )"', '"{\"A\":\"①dù ②bù ③yú\",\"B\":\"①dú ②fǒu ③chàn\",\"C\":\"①dòu ②bù ③chàn\",\"D\":\"①dòu ②fǒu ③yú\"}"', 'A', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.842 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列依次对句子中的“师”字的解释，正确的一项是（ )"', '"{\"A\":\"学习 老师 从师 老师\",\"B\":\"老师 老师 学习 从师\",\"C\":\"老师 从师 学习 老师\",\"D\":\"学习 从师 老师 老师\"}"', 'A', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.848 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列句子中加点的字，与“于其身也，则耻师焉”中的“耻”字，用法不相同的一项（)"', '"{\"A\":\"成以其小，劣之\",\"B\":\"登泰山而小天下\",\"C\":\"吾妻之美我者，私我也\",\"D\":\"焚百家之言，以愚黔首\"}"', 'A', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.854 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列每组人物，在“从师态度”上没有形成对比的一项是（ ）"', '"{\"A\":\"圣人与众人\",\"B\":\"圣人与愚人\",\"C\":\"巫医乐师百工之人与士大夫\",\"D\":\"童子之师与传道解惑者\"}"', 'D', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.860 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"对文中所说的“传其道解其惑”中的“道”，理解正确的一项是（ )"', '"{\"A\":\"一般人都应该懂得的大道理\",\"B\":\"主要指封建社会的道德标准\",\"C\":\"儒家经典中体现出来的儒家学说\",\"D\":\"古人对于宇宙运行规律的一种认识\"}"', 'B', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.865 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对文段内容的理解与分析，不正确的一项是（ )"', '"{\"A\":\"文段连用对比，突出了要改变社会中耻于相师这一风气的紧迫性。\",\"B\":\"文段通过多个例证，反映了社会中耻于相师这一现象的普遍性。\",\"D\":\"文段陈述了师道不复的不良风气，进而表达了自己的不满和担忧。\"}"', 'B', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.871 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2020, '2020年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全部相同的一项是（ ） [cite: 895]"', '"{\"A\":\"竞相 相左 相提并论\",\"B\":\"几许 茶几 几次三番\",\"C\":\"角逐 角色 勾心斗角\",\"D\":\"供职 供养 供不应求\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.387 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"[cite_start]下列各组词语，书写完全正确的一项是（ ） [cite: 900]"', '"{\"A\":\"请缨 赶时髦 火中取粟\",\"B\":\"疲蔽 连轴转 管中窥豹\",\"C\":\"集锦 合订本 凤毛麟角\",\"D\":\"誊清 大杂会 老骥伏枥\"}"', 'B', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.391 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"[cite_start]依次填入下面一段文字横线处的词语，最恰当的一项是（ ） [cite: 905]"', '"{\"A\":\"基准 演示 随着\",\"B\":\"基础 演变 随着\",\"C\":\"基准 演变 随同\",\"D\":\"基础 演示 随同\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.395 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2020, '2020年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用， 不正确 的一项是（ ) [cite: 911]"', '"{\"A\":\"在游艺比赛中，体育生占尽优势，不费吹灰之力，迅速闯关，率先到达终点。\",\"B\":\"作为一代拳王，他在巅峰时期号称 “不败王者” ，三下五除二就击倒了对手。\",\"C\":\"记者获悉，这家俱乐部引进外籍球员的事还在报价阶段，加盟八字没一撇呢。\",\"D\":\"为了保证比赛的公平性，八分之一决赛唱对台戏，四个队两场比赛同时进行。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.398 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2020, '2020年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ) [cite: 916]"', '"{\"A\":\"这支球队新赛季主力阵容终于亮相，未来成绩如何，众说纷纭，见仁见智。\",\"B\":\"发令枪一响，运动员们就像离弦的箭一样飞快冲出去，竞争场面触目惊心。\",\"C\":\"在农民趣味运动会中， “赶着大鹅奔小康” 的比赛别开生面，格外引人注目。\",\"D\":\"体育营销要以心理效应为主，长期努力才能实现各种收益，不能一蹴而就。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.402 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 921]"', '"{\"A\":\"他表示，将在隔离的14天里进行室内训练，然后回到球场，打好每一场比赛。\",\"B\":\"英超联赛如果跟随政府的防疫思路，本赛季、甚至下个赛季的比赛都会受影响。\",\"C\":\"为期三天的展会，将展演各种有地方特色的传统体育活动，展现传统体育魅力。\",\"D\":\"体育所包含的价值观念、精神状态以及文化认知等，一定都会随着时代的变化。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.408 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]在横线处填入下列句子或短语，顺序最恰当的一项是（ ） [cite: 926]"', '"{\"A\":\"⑤②③①④\",\"B\":\"⑤③①④②\",\"C\":\"③①②⑤④\",\"D\":\"③②①④⑤\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.412 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]下面各句中使用的修辞手法，与其他三项不同的一项是（ ） [cite: 937]"', '"{\"A\":\"洱海，这面光洁的梳妆镜。南北长百里，东西宽十余里。\",\"B\":\"香溪如歌如泣地前行，越看越感到亲切并喜欢它的风格。\",\"C\":\"远远地看见一座高塔耸立在山坡上，许多绿树拥抱着它。\",\"D\":\"车在奔驰，风在欢笑，成熟的晚稻，沉沉地压在大地上。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.417 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2020, '2020年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ） [cite: 942]"', '"{\"A\":\"孔子的学生把他的遗物——衣、冠、琴、车、书－－保存在他的故居，作为 “庙” 。\",\"B\":\"加缪说： “人们可以在几种意义上阅读《鼠疫》，它最明显的意义就是抵抗纳粹。”\",\"C\":\"“牛最喜欢吃这些茅草，你留着反正也是没有用的……” 他喃喃地说，赶牛走了。\",\"D\":\"你本该属于蓝天，却亿万年躺在地上，被苦涩腌渍。我该向你说什么？青海湖？\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.421 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]下列有关作家作品的表述， 不正确 的一项是（ ） [cite: 947]"', '"{\"A\":\"《桃花源记》是汉末陶渊明的散文。\",\"B\":\"《醉翁亭记》是北宋欧阳修的散文。\",\"C\":\"《女神》是现代作家郭沫若的诗集。\",\"D\":\"《复话》的作者是列夫·托尔斯泰。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.425 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2020, '2020年体育单招文化考试', '"下列词语中加点字的读音，不正确的一项是（ )"', '"{\"A\":\"木屐（jī)\",\"B\":\"脍炙人口（zhì)\",\"C\":\"嵌进（qiàn)\",\"D\":\"鳞次栉比（jié)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.429 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ )"', '"{\"A\":\"通衢：四通八达的道路。\",\"B\":\"引人入胜：让人引以为豪。\",\"C\":\"联袂：这里是“相连”之意。\",\"D\":\"沧海桑田：形容世事巨变。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.433 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"第4段写到父亲决定带我去街上看月。下列有关理解和分析，不正确（ )"', '"{\"A\":\"“我”的喊声引发父亲看月的好奇心，唤醒他美好的记忆。\",\"B\":\"“我”的喊声让父亲突然想起下街头才是看月的绝佳之地。\",\"C\":\"父亲“忽然想起什么似的”，表明他是临时决定去看月的。\",\"D\":\"父亲“立即拉着我下楼”，表现出他亲切果决的性格特点\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.437 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对第6段中“那练潭秋月最不同凡响”的理解和分析，正确的一项（ )"', '"{\"A\":\"“练潭秋月”，远处有山影，近处有苇、荷，风景宜人，所以不同凡响。\",\"B\":\"“练潭秋月”，为“桐城八景“之，因为与父亲同游，所以不同凡响。\",\"C\":\"“练潭秋月”，有天上月，水中月，还有弯月形的水潭，所以不同凡响。\",\"D\":\"“练潭秋月”让我痴迷留恋，生出如真似幻般的感觉，所以不同凡响。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.442 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对第7段中“竟磨灭了那月的灵性”的理解，正确的一项是（ ）"', '"{\"A\":\"“那月” ，指天上的月亮； “磨灭灵性” ，指云遮雾掩。\",\"B\":\"“那月” ，指水中的月亮； “磨灭灵性” ，指模糊不清。\",\"C\":\"“那月” ，指心中的月亮， “磨灭灵性” ，指记忆续薄。\",\"D\":\"“那月” ，指潭水的形状， “磨灭灵性” ，指湖水变窄。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.446 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"文中写“我”三次到练潭，下列有关概据分析。不正确的一项是（ ）"', '"{\"A\":\"童年时代到练潭，老街繁华，月色美好，湖水清澈，铭刻在记忆里永远不忘怀。\",\"B\":\"青年时代到练潭，小镇清冷，月色依旧，湖水萎缩，大有寻梦不遇的失落之感。\",\"C\":\"中年时代到练潭，古镇衰落，老街残破，感叹铁路公路的出现破坏了古老文明。\",\"D\":\"“我” 第一次到练潭是十分偶然的。后两次去则是 “寻梦之旅” ，寄寓颇多感慨。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.452 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"对文章首尾两段的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"首段指出月的三种存在形式，尾段回忆这三种月的状态，遥相呼应。\",\"B\":\"首段没有提及练潭，尾段处处追述练潭，首尾虚实互见，相映生辉。\",\"C\":\"首段凌空起笔，格调高品，节奏明快，浓烈的诗意和情韵溢于言表。\",\"D\":\"尾段徐徐落笔，格调低沉，节奏舒缓，浓重的忧愁和焦虑表露无遗。\"}"', 'C', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.876 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列选项中，最适合做文章标题的是（ ）"', '"{\"A\":\"练潭观月\",\"B\":\"练潭对月\",\"C\":\"练潭寻月\",\"D\":\"练潭梦月\"}"', 'B', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.882 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2020, '2020年体育单招文化考试', '"下列对文中加点字的注音，不正确的一项是（ )"', '"{\"A\":\"霰（xiàn)\",\"B\":\"壑（hè)\",\"C\":\"庇（bi)\",\"D\":\"禀（lǐn)\"}"', 'B', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.888 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"下列对文中画线词语的解释， 不正确 的一项是（ ）"', '"{\"A\":\"扶疏：枝叶茂盛错落。\",\"B\":\"卓然：高直的样子。\",\"C\":\"交零：交替着落下来。\",\"D\":\"彰闻：广泛地传闻。\"}"', 'A', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.894 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对文中“岂徒风朝月夕为耳目之资者哉？”一句的翻译，正确的一项是（ ）"', '"{\"A\":\"难道只是在早晨晚上欣赏风景作为视听的凭借吗？\",\"B\":\"难道只有在早晨晚上才有闲暇有心情欣赏风景吗？\",\"C\":\"难道人们在早晨有风晚上有月时不能欣赏风景吗？\",\"D\":\"难道人们在有风有月之夜没有欣赏风景的凭借吗？\"}"', 'C', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.900 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对“丝桐间发，莫能拟其声”一句话的理解，正确的一项是（ ）"', '"{\"A\":\"“丝桐间发” ，指桐树发出声音； “莫能拟其声” ，指不能模仿松树的声音。\",\"B\":\"“丝桐间发”，指桐树发出声音；“莫能拟其声”，指不能比拟松树的品格。\",\"C\":\"“丝桐间发”，指琴弦发出声音；“莫能拟其声”，指不能模仿松树的声音。\",\"D\":\"“丝桐间发”，指琴弦发出声音；“莫能拟其声”，指不能表现松树的品格。\"}"', 'B', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.907 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对本文内容和修辞手法的理解， 不正确 的一是（ ）"', '"{\"A\":\"作者将松树比作圣贤，赋予它种种美德，认为它可以启发帝王制定礼法。\",\"B\":\"作者认为参悟松树可以修身养性，使人清廉有节，正直不阿，彰明令德。\",\"C\":\"全篇多用对偶句，前呼后应，抑扬顿挫，具有整齐凝练、声韵和谐之美。\",\"D\":\"全篇多用排比句，气势充沛，感情强烈，具有节奏明快、意脉融贯之美。\"}"', 'B', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.913 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列有关“岁寒堂”命意由来的判断，正确的一项是（ ）"', '"{\"A\":\"出自《庄子·让王》：“孔子曰：“天寒既至，霜雪既降，吾是以知松柏之茂也。”\",\"B\":\"出自《荀子·大略》：“岁不寒无以知松柏，事不难无以知君子。”\",\"C\":\"出自《淮南子·俶真训》：“夫大寒至，霜雪降，然后知松柏之茂也。”\",\"D\":\"出自《论语·子罕》：“子曰：“岁寒，然后知松柏之后凋也。＇”\"}"', 'C', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.919 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2021, '2021年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全部相同的一项是（ ） [cite: 716]"', '"{\"A\":\"兴致 兴奋 云兴霞蔚\",\"B\":\"范畴 酬劳 运筹帷幄\",\"C\":\"更迭 更加 改弦更张\",\"D\":\"弊端 瞥视 敝帚自珍\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.457 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"[cite_start]下列各组词语，书写完全正确的一项是（ ） [cite: 721]"', '"{\"A\":\"问鼎 逆时针 雷霆万钧\",\"B\":\"荣膺 间奏曲 戮力同心\",\"C\":\"请樱 殊不知 甚器尘上\",\"D\":\"抛锚 书券气 旁征博引\"}"', 'B', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.461 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"[cite_start]依次填入下面一段文字横线处的词语，最恰当的一项是（ ） [cite: 721]"', '"{\"A\":\"导向 转移 解决\",\"B\":\"导向 转变 解释\",\"C\":\"方向 转移 解释\",\"D\":\"方向 转变 解决\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.465 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2021, '2021年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用，不正确的一项是（ ) [cite: 722]"', '"{\"A\":\"在决赛前的恳谈会上，队员们打开天窗说亮话，总结了技战术方面的一些弱点。\",\"B\":\"赛事营销要放长线，钓大鱼，一份长期稳定的合约无疑能给双方带来更多好处。\",\"C\":\"对方谈判代表总是闪烁其词，当面锣，对面鼓，让人捉摸不透他们的真实意图。\",\"D\":\"羊毛出在羊身上，这些球星通过一些商业活动，很快赚回俱乐部高昂的转会费。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.469 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2021, '2021年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ） [cite: 726]"', '"{\"A\":\"精神追求与身体实践的兼容并蓄，蕴含着中国博大精深的传统体育文化理念。\",\"B\":\"昔日活跃于街头巷尾的传统体育项目在城市化的进程中销声匿迹，令人担忧。\",\"C\":\"在任今天的足球比赛中，他是第一个进球的始作俑者，突显出名将的迷人风采。\",\"D\":\"最终，她凭着坚定的意志和自信心，力挽狂澜，为全队夺冠立下了汗马功劳。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.475 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 731]"', '"{\"A\":\"如何运用人工智能技术发展体育事业，是每个体育工作者必须思考的大问题。\",\"B\":\"你知不知道，体育比赛不仅关乎个人，而更多的是关乎团队甚至国家民族吗？\",\"C\":\"马拉松赛的仪式除了具备一般体育赛事的组织特征，还具有自己的文化特征。\",\"D\":\"赛会的发展，明星的效应，品牌的塑造，都因体育传播产生巨大的商业价值。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.480 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]在横线处填入下列句子，顺序最恰当的一项是（ ） [cite: 733]"', '"{\"A\":\"②①③⑤④\",\"B\":\"⑤④③②①\",\"C\":\"①②③⑤④\",\"D\":\"④③⑤②①\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.485 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]下列各句中使用的修辞手法，与其他三项不同的一项是（ ） [cite: 735]"', '"{\"A\":\"罗布泊像钟摆一样一次次地位移，逐水而居地罗布人总是在搬迁。\",\"B\":\"雪水使河道很快地恢复了昔日地样子，在阳光中如一条起伏地丝带。\",\"C\":\"每天傍晚，夕阳看我的目光虽然不再锐利，但却有着期待与鼓舞。\",\"D\":\"草地变成了明镜似地湖泊，昔日地湖泊又形成了奇形怪状地石山。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.489 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2021, '2021年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ） [cite: 736]"', '"{\"A\":\"亭侧的巨石上，刻着篆书的《醉翁亭》三个大字，碑石斜卧，似呈醉态。\",\"B\":\"哦，好一座威武庄严的雄关！山海关，这号称“天下第一关”的山海关！\",\"C\":\"仰头看吧！天都峰，果然像过去的旅行家描写的那样－“卓绝云际”。\",\"D\":\"是的，如果没有了园，那还叫江南吗？我的心里，一阵孤独，一阵复杂。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.495 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]下列有关作家作品的表述 不正确 的一项是（ ） [cite: 736]"', '"{\"A\":\"《师说》是唐代文学家韩愈的一篇散文。\",\"B\":\"《钱塘湖春行》是北宋苏轼的一首七律诗。\",\"C\":\"《故乡》是鲁迅小说集《呐喊》中的一篇。\",\"D\":\"《飞鸟集》是印度诗人泰戈尔的代表作品。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.500 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2021, '2021年体育单招文化考试', '"下列加点字的读音， 不正确 的一项是（ ）"', '"{\"A\":\"屏住（pǐng)\",\"B\":\"小心翼翼（yì)\",\"C\":\"参半（cān)\",\"D\":\"作茧自缚（fù)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.506 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ ）"', '"{\"A\":\"蠢蠢欲动：形容蠢笨乱作一团的样子。\",\"B\":\"归宿：最终的着落。\",\"C\":\"如释重负：形容解除压力的轻松愉快。\",\"D\":\"浸渍：用液体泡着。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.511 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"对第1段中“房间里像是下起了小雨，沙沙作响”一句，理解和分析正确的一项是（ ）"', '"{\"A\":\"母亲撒桑叶的声音，像下起小雨，沙沙作响。\",\"B\":\"母亲不停地撒着桑叶，挥汗如雨，沙沙作响。\",\"C\":\"蚕贪婪地啃食着桑叶，声音急促，沙沙作响。\",\"D\":\"蚕因争食互相打斗，像下起小雨，沙沙作响。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.541 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"对第2段中“记得蚕种是论“张”的”这句话，理解和分析正确的一项是（ ）"', '"{\"A\":\"“记得”表示不能确定，“张”是表示蚕种单位的量词。\",\"B\":\"“记得”表示没有忘记，“张”是表示蚕种单位的量词。\",\"C\":\"“记得”表示不能确定，“张”是表示蚕种状态的动词。\",\"D\":\"“记得”表示没有忘记，“张”是表示蚕种状态的动词。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.547 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"对第3段中“现实越是苦闷，内心的阳光和梦想就越是强烈”这句话，理解和分析不正确的一项是（ ）"', '"{\"A\":\"现实的苦闷，强烈刺激了自尊心，让老实巴交的农民开始思考致富之路。\",\"B\":\"现实的苦闷，激发了内心的憧憬与追求，让农民坚信劳动可以改变生活。\",\"C\":\"这句话表现了那个时代农民的不甘贫苦、吃苦耐劳、愈挫愈勇的可贵精神。\",\"D\":\"这句话是作者回想、反思过去时光的结论，表现出些许的慨叹和沧桑感。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.553 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"作者在第四段谈到“束缚”和“意识”，对此的理解和分析不正确的一项是（ ）"', '"{\"A\":\"人们通常所说的“作茧自缚”其实是一种基于比喻的偏见。\",\"B\":\"“自缚”是蚕生命历程中的自然归宿，而不是它的生存困境。\",\"C\":\"“束缚”是一种有积极意义的限制，可以让人集中精力做事。\",\"D\":\"越是“勇于“自缚””的人生，就越能发挥人的潜能和价值。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.557 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"文章首尾两段都提到了“底色”一词，对其意蕴的理解和分析不正确的一项是（ )"', '"{\"A\":\"养蚕的经历，已成为“我”记忆深处挥之不去的印象。\",\"B\":\"养蚕的经历，让“我”学会看待生活和生活中的自己。\",\"C\":\"养蚕的经历，让“我”领悟到美来自劳动创造的道理。\",\"D\":\"养蚕的经历，让“我”珍惜像“茧”一样的小小家庭。\"}"', 'D', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.924 +00:00');
