INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"下列对文中画线词语的解释，不正确一项（ ）"', '"{\"A\":\"得意：称心如意。\",\"B\":\"诈胜：突然取胜。\",\"C\":\"太牢：牛羊猪齐备。\",\"D\":\"独擅：独自做主。\"}"', 'B', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.941 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列对文中“之”字用法的解释，正确的一项是（ ）"', '"{\"A\":\"“天下之至言”与“诈胜之徒”，“之”字用法相同。\",\"B\":\"“天下之至言”与“大胜之”，“之”字用法相同。\",\"C\":\"“大胜之”与“二子之言”，“之”字用法相同。\",\"D\":\"“大胜之”与“胜之徒”，“之”字用法相同。\"}"', 'D', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.946 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列对文中画线句子的翻译，正确的一项是（ ）"', '"{\"A\":\"谁知道我路过，却不向我请安，按罪刑罚。\",\"B\":\"谁知道我的错误，却不告诉我，按罪刑罚。\",\"C\":\"谁知道我的错误，却为我隐瞒，按罪刑罚。\",\"D\":\"谁知道我路过，却不向我汇报，按罪刑罚。\"}"', 'B', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.950 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列对文中有关内容的理解，正确的一项是（ ）"', '"{\"A\":\"“南面而立”，有“称王”的意思。\",\"B\":\"“西面而立”，有“称霸”的意思。\",\"C\":\"“东面而立”，有“称臣”的意思。\",\"D\":\"“南面” “西面” “东面” 皆无寓意。\"}"', 'C', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.954 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列有关这篇短文体裁和主旨的阐释，正确的一项时（ ）"', '"{\"A\":\"这是一则史传，讲述了春秋时代三个君王的故事\",\"B\":\"这是一则政论，论证了“明主者有三惧”的观点。\",\"C\":\"这是一则颂赞，歌颂了春秋时代三个君王的美德。\",\"D\":\"这是一则寓言，说明了君王“居安思危”的道理。\"}"', 'D', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.960 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2022, '2022年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全都相同的一项是（ ） [cite: 757]"', '"{\"A\":\"哈欠 哈达 点头哈腰\",\"B\":\"花苞 袍子 明碉暗堡\",\"C\":\"好转 好歹 好逸恶劳\",\"D\":\"巅峰 掂量 黑白颠倒\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.563 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2022, '2022年体育单招文化考试', '"[cite_start]下列各组词语中，书写完全正确的一项是（ ） [cite: 762]"', '"{\"A\":\"编纂 闷葫芦 世外桃园\",\"B\":\"辐射 微循环 偃旗息鼓\",\"C\":\"表彰 峰窝煤 雷厉风行\",\"D\":\"临摹 绊脚石 灸手可热\"}"', 'B', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.567 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2022, '2022年体育单招文化考试', '"[cite_start]依次填人下面一段文字横线处的词语，最恰当的一项是（ ） [cite: 767]"', '"{\"A\":\"创造 提升 促进\",\"B\":\"打造 提升 促进\",\"C\":\"打造 提高 促成\",\"D\":\"创造 提高 促成\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.572 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2022, '2022年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用，不正确的一项是（ ） [cite: 773]"', '"{\"A\":\"这是我每天回家的路，太熟悉了，闭着眼睛捉麻雀，天再黑也不会走错。\",\"B\":\"冰冻三尺非一日之寒，看这姿势就知道他练习滑雪已不是一天两天了。\",\"C\":\"妈妈回来一看，妹妹正坐在地上嚎啕大哭，不分青红皂白就把我说了一顿。\",\"D\":\"我们三个人呢，怎么可能辩不过他一个?你不要长他人志气灭自家威风!\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.577 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2022, '2022年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ） [cite: 778]"', '"{\"A\":\"冰墩墩在冬奥会上可谓名副其实的“社交利器”，获得全世界的关注和喜爱。\",\"B\":\"北京冬残奧会即将开幕，中国体育代表团近百名运动员厉兵秣马，整装待发。\",\"C\":\"“开学第一课”上，同学们观看了女足亚洲杯夺冠的视频片段，纷纷拍手称快。\",\"D\":\"最近这部热播剧的片尾音乐如泣如诉，温情而深沉，听得人禁不住热泪盈眶。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.582 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 783]"', '"{\"A\":\"冬奥原创交响组曲《冰雪相约》，构思巧妙，气势磅礴，听起来令人心潮澎湃。\",\"B\":\"这部电影展示了不少既精彩又惊险的滑雪场面，比如U型池比赛、障碍追逐等。\",\"C\":\"奥林匹克运动诞生伊始，就承载着人类对“和平、团结、进步”的美好追求。\",\"D\":\"奥运场馆中那些以奉献成就精彩的志愿者，成为普通民众参与奥运的重要形式。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.587 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]在横线处填入下列句子或短语，顺序最恰当的一项是（ ） [cite: 788]"', '"{\"A\":\"②①③⑤④\",\"B\":\"⑤④③②①\",\"C\":\"①②③⑤④\",\"D\":\"④③⑤②①\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.591 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]下列各句中使用的修辞手法，与其他三项不同的一项是（ ） [cite: 799]"', '"{\"A\":\"那点儿薄薄的雪在阳光下突然害了羞，微微露出些粉色\",\"B\":\"船头飞溅的浪花，和划船的小伙儿一起唱着欢乐的歌曲\",\"C\":\"桃树，杏树，梨树，你不让我，我不让你，都开满了花赶趟\",\"D\":\"蜿蜒流淌的小河如一条碧绿的丝带，环绕着美丽的村庄\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.596 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2022, '2022年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ） [cite: 804]"', '"{\"A\":\"“哥们儿，你到底去不去?”他又说，“你再拿不定主意我可要先走了。”\",\"B\":\"今天我主要讲两个问题一即晚自习的纪律管理和假期安全教育问题。\",\"C\":\"《离骚》《天问》和《九歌》是我国伟大的浪漫主义诗人屈原的代表作品。\",\"D\":\"在以后的岁月里，我经常会想起那个瘦弱的少年，想起他那精彩的演出。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.601 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]下列有关作家作品的表述，不正确的一项是（ ） [cite: 809]"', '"{\"A\":\"姚鼐的《登泰山记》是古代写景抒情的名篇\",\"B\":\"《荷塘月色》和《背影》都是朱自清的作品\",\"C\":\"《拿来主义》选自鲁迅的散文集《朝花夕拾》\",\"D\":\"李清照的《声声慢》用很多叠字表达感情\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.606 +00:00');
