INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2022, '2022年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ ）"', '"{\"A\":\"凝神屏气：指小心翼翼，极度紧张\",\"B\":\"混混浊浊：模模糊糊，丝毫都不清晰\",\"C\":\"从容不迫：非常镇静、不慌不忙的样子\",\"D\":\"有条不紊：有条理，有次序，一点儿都不乱\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.617 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"根据文意，对“纺车声”总结不正确的一项是（ ）"', '"{\"A\":\"坚韧\",\"B\":\"固执\",\"C\":\"永恒\",\"D\":\"独特\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.622 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"对文中“果子一样成熟”理解不正确的一项是（ ）"', '"{\"A\":\"将线锤比喻成“果子”，饱含了作者的喜爱。\",\"B\":\"外祖母机械地纺出一个个线锤。\",\"C\":\"作者期待着线锤一点点成型、完美。\",\"D\":\"作者赞美外祖母的辛勤纺线。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.627 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"对文中的“世间琐事”的理解不正确的是（ ）"', '"{\"A\":\"也能成为世间大事\",\"B\":\"经纬天下的人也应该重视\",\"D\":\"从古至今，无穷无尽\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.631 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"作者认为，一个好的工厂企业应该具备的素质是（ ）"', '"{\"B\":\"做小事情\",\"C\":\"做大事情\",\"D\":\"衣被苍生\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.636 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"根据内容分析，与文章内容不相符的一项是（ ）"', '"{\"A\":\"文章从外祖母早年纺线写起，写出了整个时代的辛酸。\",\"B\":\"文章写妈妈和婶子经线，怀旧之中有一种温暖的自豪。\",\"C\":\"文章描写织布中的梭子往来，表达了小事情中也可以有大境界的思想。\",\"D\":\"文章在新衣新年以及布衣难得的感慨中，流露出传统不再的复杂情感。\"}"', 'B', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.965 +00:00');
