INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"不列对句中加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"约于身而广于世 约：约束\",\"B\":\"赏不遗贱 遗：遗漏\",\"C\":\"刑罚中于法 中：合乎\",\"D\":\"故民心离 故：故意\"}"', 'D', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.982 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"对下列句子中加点词的解说，不正确的一项是（ ）"', '"{\"A\":\"“足以明政行教”， “以”是介词，表示凭借。\",\"B\":\"“废罪顺于民”， “于”是介词，引进“顺”的对象。\",\"C\":\"“是以贤者处上而不华”， “而”是连词，表示意思的递进。\",\"D\":\"“粒食之民”， “之”放在定语和中心语之间，相当于助词“的”。\"}"', 'C', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.987 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"“为政尚相利，故下不以相害”这个复句的类型是（ ）"', '"{\"A\":\"选择复句\",\"B\":\"转折复句\",\"C\":\"递进复句\",\"D\":\"因果复句\"}"', 'D', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.993 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"下列对文中画线句子的翻译，正确的一项是（ ）"', '"{\"A\":\"竭尽智慧引导民众，但不会在这事上夸耀。\",\"B\":\"尽量用智慧引导民众，而不是去讨伐他们。\",\"C\":\"竭尽智慧引导民众，也就不会去讨伐他们。\",\"D\":\"尽量用智慧引导民众，但不会去借此夸耀。\"}"', 'D', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:31.998 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"下列对文中有关内容的理解，不正确的一项是（ ）"', '"{\"A\":\"景公向晏子请教古代盛君的问题，是想知道自己怎么做才比较好。\",\"B\":\"晏子从多个角度告诉景公古代盛君的特点，希望能够影响到景公。\",\"C\":\"景公听了晏子的回答之后，显然心里明白可是他没有做任何反应。\",\"D\":\"晏子不得已之下只好直接指出了国君的问题，景公这才明白过来。\"}"', 'C', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.003 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2023, '2023年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"重要 重量 山重水复\",\"B\":\"木拴 诠释 病情痊愈\",\"C\":\"应酬 应对 得心应手\",\"D\":\"谱写 铺垫 普天同庆\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.641 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2023, '2023年体育单招文化考试', '"下列各组词语，没有错别字的一项是（ ）"', '"{\"A\":\"斟酌 泼水节 意气风发\",\"B\":\"校对 护身符 大意凛然\",\"C\":\"遵从 微波炉 心无旁务\",\"D\":\"模擦 金雏棒 乱七八糟\"}"', 'B', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.647 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2023, '2023年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"突破 确立 占据\",\"B\":\"冲破 树立 占领\",\"C\":\"冲破 确立 占领\",\"D\":\"突破 树立 占据\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.653 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2023, '2023年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"单位给他涨工资了，他却洋洋得意地说了些漂亮话，真是得了便宜还卖乘!\",\"B\":\"有那么一些人做起事情来总是前怕狼,后怕虎，顾虑太多，结果一事无成。\",\"C\":\"比赛中出现的失误反而让观众记住了他的名字，他这是捡了芝麻，丢了西瓜。\",\"D\":\"不入虎穴，焉得虎子?要想取得成功，就要有敢闯敢拼、不怕困难的奋斗精神。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.659 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2023, '2023年体育单招文化考试', '"下列各句中加点成语的使用，不正确的一项是（ ）"', '"{\"A\":\"北京冬奥会成功举办后不到一年的时间里，冰雪运动在全国各地已经蔚然成风。\",\"B\":\"在现场观众捶胸顿足的背后，不知凝聚了运动员们多少个日日夜夜的辛勤付出。\",\"C\":\"正在举行的全国两会上，委员们就如何加快建设体育强国各抒己见，献计献策。\",\"D\":\"参加此次世界杯的五位中国选手在首轮比赛中都旗开得胜，顺利晋级第二阶段。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.663 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列句子中，有语病的一项是（ ）"', '"{\"A\":\"随着春节假期的逐渐临近，全球各地旅游相关行业都在热切期待着中国游客的到来。\",\"B\":\"运动健身不仅是身体免疫力和生活质量的重要途径，也是建设健康中国的必然要求。\",\"C\":\"龙舟、围棋和太极拳等中华传统体育项目已走出国门，向世界展示中华文化的智慧。\",\"D\":\"现在，人们对运动的个性化需求不断增加，像马术这类小众项目也越来越受到行睐。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.668 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ ）"', '"{\"A\":\"⑤③④②①\",\"B\":\"①④⑤③②\",\"C\":\"②④①⑤③\",\"D\":\"④①⑤③②\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.674 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列各句中，没有使用夸张手法的一项是（ ）"', '"{\"A\":\"白发三千丈，缘愁似个长。\",\"B\":\"三杯两盏淡酒，怎敌他、晚来风急!\",\"C\":\"飞流直下三千尺，疑是银河落九天。\",\"D\":\"山，快马加鞭未下鞍。惊回首，离天三尺三。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.679 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2023, '2023年体育单招文化考试', '"下列句子中标点符号的使用不正确的一项是（ ）"', '"{\"A\":\"“鸟语花香”,“桃李争艳”,“”草长莺飞，这三个词语都是用来描绘春天的。\",\"B\":\"什么事情该做，什么事情不该做，都需要做父母的时刻来提醒孩子。\",\"C\":\"社会习惯的改变——这常常是社会变革带来的结果，会对语言产生影响。\",\"D\":\"我独自站在那喃喃自语：“我怎么欺骗他了?我伤害他了?还是我侮辱他了?”\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.683 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是（ ）"', '"{\"A\":\"“先天下之忧而忧”出自欧阳修的《醉翁亭记》。\",\"B\":\"闻丁多的代表作包括，《红烛》《死水》等。\",\"C\":\"“豆腐西施”杨二嫂是鲁迅小说《故乡》中的人物。\",\"D\":\"杜甫名作《登高》营造了沉郁悲凉的意境。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.687 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2023, '2023年体育单招文化考试', '"对下列词语中加点字的注音，不正确的一项是 （ ）"', '"{\"A\":\"惆怅(chàng)\",\"B\":\"虔敬(qián)\",\"C\":\"沮丧(sāng)\",\"D\":\"阴霾(mái)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.692 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2023, '2023年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是 （ ）"', '"{\"A\":\"枝叶繁茂：枝和叶长得很密很盛。\",\"B\":\"萎靡：形容意志消沉的样子。\",\"C\":\"参差有致：高低不一，错落有致。\",\"D\":\"亮丽：形容色彩明亮的样子。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.697 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"对文中野樱桃移植和养护场景的理解和分析，不正确的一项是 （ ）"', '"{\"A\":\"隐含了对外祖父的怀念。\",\"B\":\"表现了对故乡植物的珍视。\",\"C\":\"表现了难离故土的情感。\",\"D\":\"为下文妈妈的去世做了铺垫。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.701 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"对文中“妈妈、爸爸，这是女儿所能给你们的最好的礼物了”的理解，不正确的一项是 （ ）"', '"{\"A\":\"表明妈妈和爸爸多年来对故乡十分牵挂。\",\"B\":\"表明女儿对妈妈、爸爸的心意非常了解。\",\"C\":\"表明女儿的故乡之行其实是代父母去的。\",\"D\":\"表明子女对父母精神生活的关注很重要。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.707 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列各句中，使用比喻修辞的一项是 （ ）"', '"{\"A\":\"好奇与激动的程度不亚于太空人登月球。\",\"B\":\"我幻想着他们接过了泥土的小胶袋，也同时接过一大串属于他们的美好年月。\",\"C\":\"盆里的两株小兰芽，泛着比星星还耀眼的光芒。\",\"D\":\"几片叶子立得笔直，如同最绿的翡翠。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.713 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"“照例是个黯淡的黄昏，照例地拿着铜质小水壶，给花窗上的几盆花草做三天一次的浇水。”对句中两处“照例”的理解和分析，不正确的一项是 （ ）"', '"{\"A\":\"通过词语的意义不经意间演绎了时间的流淌。\",\"B\":\"表明作者的生活很刻板，没有什么开心的事。\",\"C\":\"说明作者这样的生活节奏已持续了一段时间。\",\"D\":\"与下文发现新芽的惊喜形成反差的艺术效果。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.717 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列理解和分析，与原文意思不相符的一项是 （ ）"', '"{\"A\":\"文章首段称故乡之行为“人生旅途上的高潮”,饱含着作者对故乡的情感。\",\"B\":\"文中“我”和`家人都叫不知名的兰花为“中国兰”,寄托了海外游子的情思。\",\"C\":\"作者一度认为“故乡的泥土是死土”,表明作者的情绪受到周围事物的影响。\",\"D\":\"重生的中国兰带有一股浓郁扑鼻的芳香，由此转变了作者对故乡泥土的看法。\"}"', 'D', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.008 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是 （ ）"', '"{\"A\":\"中国兰\",\"B\":\"故乡的泥土\",\"C\":\"故乡行\",\"D\":\"故乡的礼物\"}"', 'A', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.013 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2023, '2023年体育单招文化考试', '"下列对文中加点字的注音，不正确的一项是 （ ）"', '"{\"A\":\"昔者晋献公欲假道于虞以伐虢(jia)\",\"B\":\"赂虞公(lu)\",\"C\":\"马犹取之内原而著之外既也(zhù)\",\"D\":\"还反处三年(huán)\"}"', 'C', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.018 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列对文中加点词的解释，不正确的一项是 （ ）"', '"{\"A\":\"求假道焉 求：请求。\",\"B\":\"君勿忧 忧:担心。\",\"C\":\"荀息伐虢克之 克：攻克\",\"D\":\"献公说曰 说；说话\"}"', 'B', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.024 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"对下列句子中“之”字的解说，不正确的一项 （ ）"', '"{\"A\":\"吾先之君宝也，“之”放在定语与名词之间，略等于现代汉语的“的”\",\"B\":\"虞公贪利其璧与马而欲许之，“之”代指前文的“璧与马”\",\"C\":\"夫虞之有虢也，如车之有辅，“之”放在主语谓语之间，取消句子独立性\",\"D\":\"荀息伐虢克之，“之”略等于现代的他\"}"', 'D', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.029 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"“若假之道，则虢朝亡而虞夕从之矣”。这个复句的类型是 （ ）"', '"{\"A\":\"选择复句\",\"B\":\"转折复句\",\"C\":\"假设复句\",\"D\":\"因果复句\"}"', 'C', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.035 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列对文中画线句子的翻译，正确的一项是 （ ）"', '"{\"A\":\"璧还和以前一样。即便如此，马还是长儿岁了。\",\"B\":\"璧还和以前一样。即便如此，但马的牙齿长好了。\",\"C\":\"虽然马的牙齿更加长了，但璧还是和以前一样。\",\"D\":\"璧还和以前一样，但马的牙齿和从前不一样了。\"}"', 'D', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.041 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列对文中有关内容的理解，不正确的一项是 （ ）"', '"{\"A\":\"晋献公对于自己的璧与马十分喜爱，担心虞公收了礼物却不借道\",\"B\":\"荀息认为把宝物送给虞公，只是换了个地方收藏，迟早会拿回来\",\"C\":\"虞公不听宫之奇的劝谏，贪图晋国的璧与马，最终导致国家灭亡\",\"D\":\"荀息攻克了虢国后，顺道攻克虞国，夺回了璧与马，献公很高兴\"}"', 'A', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.047 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2024, '2024年体育单招文化考试', '"下列各组词语中加点的字，读音全部相同的一项是（ ）"', '"{\"A\":\"宿舍 舍妹 退避三舍\",\"B\":\"篓子 槛楼 屡教不改\",\"C\":\"依畏 猥琐 泾渭分明\",\"D\":\"会客 会计 心领神会\"}"', 'A', '', '3', 1, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.724 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2024, '2024年体育单招文化考试', '"下列各组词语，书写完全正确的一项是（ ）"', '"{\"A\":\"示意 拦路虎 自爆自弃\",\"B\":\"玉佩 赶时髦 筹踞满志\",\"C\":\"编缉 凌分子 妙趣横生\",\"D\":\"果腹 金刚钻 相濡以沫\"}"', 'B', '', '3', 2, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.732 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2024, '2024年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"抵御 特定 损害\",\"B\":\"抵御 特制 损伤\",\"C\":\"抵抗 特制 损害\",\"D\":\"抵抗 特定 损伤\"}"', 'C', '', '3', 3, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.737 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2024, '2024年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"小李新买了一个飞船模型，兴冲冲地拿去找同桌一起玩，没想到碰了一鼻子灰。\",\"B\":\"产品设计如果一味追求标准化和模式化，不敢越雷池一步，势必丧失创新活力。\",\"C\":\"面试官问了几个挺简单的专业问题，不料他却支支吾吾，回答得驴唇不对马嘴。\",\"D\":\"今天，美术馆里刚展出了一幅人物肖像画，面像里的人物惟妙惟肖，有鼻子有限。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.742 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2024, '2024年体育单招文化考试', '"下列各句中加点成语的使用，不正确的一项是（ ）"', '"{\"A\":\"昨天学校的游泳比赛上，他又一次当仁不让地拿到了冠军。\",\"B\":\"姐姐当时没说什么，分别后写信来劝他，句句是金玉良言。\",\"C\":\"东方艺术大楼的飞檐、斗拱、琉璃瓦古色古香，极具韵味。\",\"D\":\"听说家中变故，他五内俱焚，急忙乘坐最早的航班赶回去。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.748 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列句子中，有语病的一项是（ ）"', '"{\"A\":\"坚持体育锻炼有助于保持心理健康，降低患神经退行性疾病的风险。\",\"B\":\"针对老人、小孩及生活不能自理者，使用本产品时应有成年人看护。\",\"C\":\"在较粗组织损伤的慢性期，热敷有一定地缓解疼痛，促进恢复的效果。\",\"D\":\"以不确定、叠加、纠缠等为特征的量子是一种崭新的思维方式。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.753 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ ）"', '"{\"A\":\"①②③④\",\"B\":\"①③②④\",\"C\":\"④①③②\",\"D\":\"③④①②\"}"', 'C', '', '3', 7, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.758 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列各句中使用的修辞手法，与其他三项不同的一项是（ ）"', '"{\"A\":\"他在房间默默出神的时候，一道青白的电光猛地撕裂了漆黑的夜空。\",\"B\":\"窗台上的水仙花清丽脱俗，如同身着白衣裙、亭亭玉立的河边少女。\",\"C\":\"我打了个寒颤，我就知道，我们之间已经隔了一层可悲的屏障壁了。\",\"D\":\"极光有时出现时间极短，如节日的焰火在空中闪现一下又马上消失。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.762 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2024, '2024年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"北京紫禁城一共有四座城门：午门、神武门、东华门和西华门。\",\"B\":\"课堂上，小明问：“老师，‘有条不紊’的‘素’是什么意思？”\",\"C\":\"以《时间的故事》为主题的数字互动体验展终于落户北京鼓楼。\",\"D\":\"同伴邀我一起去，我心情不好，淡淡地说：“算了，你们去吧。”\"}"', 'A', '', '3', 9, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.768 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是（ ）"', '"{\"A\":\"繁琐和鲁莽都是曹禺的活剧《雷雨》中的人物。\",\"B\":\"《春江花月夜》是唐代诗人张若虚对月抒情的名篇。\",\"C\":\"《繁星》《春水》和《新月集》都是冰心所著诗集。\",\"D\":\"韩愈在散文《师说》中慨叹“师道之不传也久矣！”\"}"', 'B', '', '3', 10, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.774 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2024, '2024年体育单招文化考试', '"下列词语中加点字的读音，不正确的一项是（ ）"', '"{\"A\":\"余脉 (mài)\",\"B\":\"宽绰 (chuò)\",\"C\":\"踌躇 (màn)\",\"D\":\"村寨 (zhài)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.778 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2024, '2024年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ ）"', '"{\"A\":\"横竖：指把一物体嵌入另一物体内\",\"B\":\"临歧：指来到从大路分出的岔路。\",\"C\":\"奇妙：指难得的货物。\",\"D\":\"须臾：指较长的时间。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.784 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对第2段引用苏东坡《岐亭五首（并叙）》中诗句的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"照应前文所提岐亭的位置及几幅山的环境。\",\"B\":\"引出下文中对陈季常相符苏东坡吃饭的铺叙。\",\"C\":\"再现了苏东坡与陈季常之间长久而深厚的友情。\",\"D\":\"体现出与写作“无为在歧路，儿女共沾巾”的主物的不同人生追求。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.789 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对“哪有我们这般故人相逢灯火可亲有情味”句中“情味”的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"在风雪天里，出汗穴，一路风坐仆仆，终于遇见故人相逢，感到一种安慰。\",\"B\":\"在朋友家中受到热情接待，特别新鲜丰盛的酒菜，让人产生大快朵颐的满足。\",\"C\":\"陈季常不同于其父，待人亲切平等，让苏东坡又父子二人为人上的深刻差别。\",\"D\":\"二十年后重逢，无论成仕成隐，彼此都入到中年，对人生有了更多沧桑感慨。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.794 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列各句中，使用了比喻修辞的一项是（ ）"', '"{\"A\":\"大别山的余脉几幅山河完全呈现出来，果然是像九颗大小不一的螺蛳壳。\",\"B\":\"那是多么好的一餐饭，足以慰风雪，慰风尘。\",\"C\":\"苏东坡回忆起他在城北见到陈季常时，四公子骑马射箭的英姿。\",\"D\":\"《方山子传》里却说是“适见焉”“亦要然”，好像两人眼面相逢，彼此大吃一惊。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.799 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对文中有关语句的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"指出岐亭在“往西省”和“往东南”各通往哪里，证明岐亭得名是有道理的。\",\"B\":\"“骑马出汗穴，而周口，而满川”，既标示行进路线，也表明一路上心情是轻快的。\",\"C\":\"根据“季常兄项君‘高帽子’去捉鸭鹅，有助于表现脸上陈季常的性情和真诚。”\",\"D\":\"“话题呢，说不完没不完”，通过重复，表现了两人与别人重逢后追切的交流愿望。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:07:00.803 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列理解和分析，与原文意思不相符的一项是（ ）"', '"{\"A\":\"文章详细描述岐亭地理位置，罗列周边山谷和路线，是因为岐亭在古代就位量显赫。\",\"B\":\"作者在描述该考察的较时，用了设问和反问句式，代入了对苏东坡诗作的理解。\",\"C\":\"作者认为，《岐亭五首（并叙）》中苏东坡表现出对早年才子时期心态的超越。\",\"D\":\"文章引用《方山子传》中的材料，认为“岐亭”还是“岐亭”的问题很难有一个定论。\"}"', 'B', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.053 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是（ ）"', '"{\"A\":\"岐亭记\",\"B\":\"儿女共沾巾\",\"C\":\"中年\",\"D\":\"友人陈季常\"}"', 'D', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.060 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2024, '2024年体育单招文化考试', '"下列对文中加点字的注音，不正确的一项是（ ）"', '"{\"A\":\"重耳无粮，寝不能行 (nēi)\",\"B\":\"子推割股肉以食重耳 (shī)\",\"C\":\"使骖乘于国中 (cān)\",\"D\":\"然君诚故之罪，与骖乘 (shè)\"}"', 'C', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.065 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对于列加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"晋文公重耳亡，亡：逃亡\",\"B\":\"天下莫不闻 莫：没有谁\",\"C\":\"于是里兔须造见，到去\",\"D\":\"臣之为贼亦大矣，小偷\"}"', 'D', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.071 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对于列各组句子中加点词的解说，不正确的一项是（ ）"', '"{\"A\":\"“子推割股肉以食重耳”，“以”是造词，表示目的。\",\"B\":\"“君沐邪”，“邪”是造词，表示疑问。\",\"C\":\"“然君诚故之罪，与骖乘”，“然”是造词，表示转折。\",\"D\":\"“夫里兔须且不诛而骖乘”，“且”是造词，表示并列。\"}"', 'A', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.076 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列对文中画法误读部分的断句，正确的一项是（ ）"', '"{\"A\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐何/言之性也\",\"B\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐何/言之性也\",\"C\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐何/言之性也\",\"D\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐/何言之性也\"}"', 'C', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.081 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列对文中画法误读的句子中的翻译，正确的一项是（ ）"', '"{\"A\":\"离开国家太久，臣下和百姓大多超过了您；您虽返国家，百姓都自觉处境危险。\",\"B\":\"离开国家太久，臣下和百姓大多轻率于您；您虽返国家，百姓都自觉处境危险。\",\"C\":\"离开国家太久，臣下和百姓大多超过了您；您虽返国家，百姓都自觉处境危险。\",\"D\":\"离开国家太久，臣下和百姓大多轻率于您；您虽返国家，百姓都自觉处境危险。\"}"', 'C', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.088 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列对文中有关内容的理解，不正确的一项是（ ）"', '"{\"A\":\"里兔须曾经偷窃流亡中的公子重耳的资料，导致公子重耳被捕。\",\"B\":\"公子重耳则曾以为晋国国君，就得到了晋国臣子和百姓的拥戴。\",\"C\":\"里兔须求见晋文公，说他可以安定晋国，最初遭到晋文公拒绝。\",\"D\":\"晋文公优待里兔须，以此表明自己不念旧恶，安定了晋国民心。\"}"', 'D', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 06:41:53.439 +00:00', '2025-12-08 08:45:32.095 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2015, '2015年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"藩篱 樊笼 翻天覆地\",\"B\":\"几案 肌理 反唇相讥\",\"C\":\"揭露 露底 风餐露宿\",\"D\":\"结实 结余 结党营私\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列各组词话中，书写完全正确的一项（ ）"', '"{\"A\":\"温馨 墨许 渺无人烟\",\"B\":\"启蒙 冥想 矫往过正\",\"C\":\"鹅黄 雀跃 闻鸡起舞\",\"D\":\"藐视 扼腕 花拳秀腿\"}"', 'C', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2015, '2015年体育单招文化考试', '"依次填入下列横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"调试 体貌 功能\",\"B\":\"调适 体貌 功绩\",\"C\":\"调试 体魄 功绩\",\"D\":\"调适 体魄 功能\"}"', 'D', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2015, '2015年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"在国际市场竞争中，虽说“胳膊拧不过大腿”，但这家小厂却凭借产品更新赢得了发展。\",\"B\":\"“墙倒众人推” ，只要我们团结一致，充分发挥集体的智慧和力量，就一定能战胜困难。\",\"C\":\"割鸡焉用牛刀？对付这样的弱队，我们主力队员不上场也完全能取胜，这个自信应该有。\",\"D\":\"对于这次比赛事故，他的辩解驴唇不对马嘴，偏离事实，自相矛盾，难以服人。\"}"', 'C', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2015, '2015年体育单招文化考试', '"下列各句中加点成语的使用，不恰当的一项是（ ）"', '"{\"A\":\"毋庸置疑，“四肢发达就是体育”的陈旧观念，还难以在社会上和学校里彻底消除。\",\"B\":\"成功体验与竞争意识相辅相成。安排比赛要考虑学生在体能、技术方面存在的差异。\",\"C\":\"保了传球、他还不断突破，急停跳投，接二连三撕开对方防线，为比分反超立下大功。\",\"D\":\"快到终点时，他的速度越来越快，强弩之末，势如破竹，终于毫无悬念的夺得了冠军。\"}"', 'D', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列句子中，没有语病的一项是（ ）"', '"{\"A\":\"预计今年电影产业会继续保持高速发展，院线银幕数量的增加，在线选座等预约服务将使电影呈现扩张态势。\",\"B\":\"这家颇负盛名的书店宣布24小时运营，引来读者无数的 “点赞” ，因为它引领了一种热爱阅读的生活方式。\",\"C\":\"近期，国内鲜奶价格不断上涨，然而受到疫情、养殖成本增加等影响，养殖户不但到了被迫卖牛的境地，甚至没有受益。\",\"D\":\"这里自晚唐五代以来就成为中国非常重要的瓷器生产核心，元代初年设计的瓷器，加强了朝廷对瓷器制造的监督和管理。\"}"', 'A', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"在横线处填入下列句子，顺序最恰当的一项是（ ）"', '"{\"A\":\"①⑤⑥②③④\",\"B\":\"③⑤⑥④②①\",\"C\":\"①⑥⑤③②④\",\"D\":\"③⑥⑤②①④\"}"', 'B', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列各句中，没有使用反问修辞手法的一项是（ ）"', '"{\"A\":\"没有了商品交换的社会，岂不要退回到自给自足的原始状态了吗？\",\"B\":\"用汉字写的国语，是否来用地方音读呢？不行！那就不是国语了。\",\"C\":\"我们已经长大成人，可是所有的大人不都是从孩童时代走来的吗？\",\"D\":\"哲学家倘若不能从心灵中汲取大部分的快乐，他算什么哲学家呢？\"}"', 'C', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2015, '2015年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"人们常会认为-----体育么？就是身体活动，是“简单的”体力活动，不需要动脑筋。\",\"B\":\"“唯分数论”观念不变，体育活动仍将是“讲起来次要，做起来次要，忙起来不要”。\",\"C\":\"一竿在手，钓趣无穷。有晨曦，有落日，有斜风，有细雨，有天光云影、鸢飞鸟啼。\",\"D\":\"突然“咔嚓”一声响，断裂的冰块漂移而去……憋闷了一个冬天的大鱼跳出水面。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是( )"', '"{\"A\":\"唐代诗人李商隐的《锦瑟》，意象朦胧，意境凄迷，情思缠绵，是脍炙人口的佳作。\",\"B\":\"巴金的散文《包身工》，表现了上世纪40年代中国女工在日本纱厂被奴役的黑暗现实。\",\"C\":\"《装在套子里的人》作者是俄国作家契诃夫，别里科夫是这篇小说中的主要人物。\",\"D\":\"《我有一个梦想》是美国黑人民权运动领袖马丁·路德·金在一次集会上的演讲。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2015, '2015年体育单招文化考试', '"下列词语中加点字的读音，不正确的一项是（ ）"', '"{\"A\":\"衡量（liáng)\",\"B\":\"塑（shù)造\",\"C\":\"时髦（máo)\",\"D\":\"勉强（qiǎng)\"}"', 'B', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2015, '2015年体育单招文化考试', '"下列对文中词语的解释，不正确的一项是（ ）"', '"{\"A\":\"望眼欲穿：形容盼望急切。\",\"B\":\"价值观：对社会事物持有的总的看法。\",\"C\":\"大举：重大的举动。\",\"D\":\"闷头：不声不响地。\"}"', 'C', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对文中语句的理解，不正确的一项是（ ）"', '"{\"A\":\"“受这个东西的奴役”，意思是人要为所买的商品付出艰辛劳动。\",\"B\":\"“在家庭财政上戳个窟窿”，意思是使家庭财政超支，出现亏空。\",\"C\":\"“挺吓人的报酬”，意思是意外的收入，令人惶恐不安。\",\"D\":\"“小有表现”，意思是在听课时略微表现出一些才能来。\"}"', 'C', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"本文三次用“一无所有”一词，对此的分析，正确的一项是（ ）"', '"{\"A\":\"第1段中的“一无所有”，形容现在的年轻人非常贫困，寄予了怜悯和同情。\",\"B\":\"第3段中的“一无所有”，形容“我们结婚”时非常贫困，有自我嘲讽意味。\",\"C\":\"第7段中的“一无所有”，提醒自己不忘过去的贫困，珍惜苦尽甘来的幸福。\",\"D\":\"本文三次用“一无所有”，都引发了如何对待年轻时生活贫困的话题和议论。\"}"', 'D', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对第4段画线部分的理解，不正确的一项是（ ）"', '"{\"A\":\"作者坚信，只要发奋图强，青年时代甘于贫困一定会有回报的。\",\"B\":\"作者认为，事业有成之后，回首青年时代的艰辛，会有幸福感。\",\"C\":\"“难道就为了100块一天给卖了？”表现出作者对报酬不太满意。\",\"D\":\"“不卖，坚决不卖！”以斩钉截铁的短句表明了作者坚定的态度。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对本文内容的理解，不正确的一项是（ ）"', '"{\"A\":\"作者讲述自己的经历，说明如何对待青春贫困与事业发展的矛盾，可谓现身说法。\",\"B\":\"作者认为，青春贫困并不可怕，可怕的是为了追逐物质享受而毁灭了青春的前途。\",\"C\":\"“最大限度地把青春留给自己” ，意为要做自己喜欢做的事，要工作，更要休闲娱乐。\",\"D\":\"文章结尾说“对裤子的所有权绝不肯放弃”，照应开头，幽默诙谐，寓意丰富深刻。\"}"', 'C', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列句中冒号的用法与其他三项不同的一项是（ ）"', '"{\"A\":\"所以，我必须想一想：这双鞋究竟有多重要？\",\"B\":\"我们有自己的理由：不把自己的青春减价出售。\",\"C\":\"还是那个道理：青春不减价拍卖。\",\"D\":\"有颇为庆幸之处：青春有价，没有贱卖。\"}"', 'B', '', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列语句，最适合做文章标题的一项是（ ）"', '"{\"A\":\"青春代价\",\"B\":\"青春定价\",\"C\":\"青春无价\",\"D\":\"青春有价\"}"', 'C', '', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2015, '2015年体育单招文化考试', '"下列句中加点字的读音，不正确的一项是（ ）"', '"{\"A\":\"酿泉为酒 酿：念niàng\",\"B\":\"山肴野萩 肴：念xiáo\",\"C\":\"宴酣之乐 酣：念hān\",\"D\":\"树林阴翳 翳：念yi\"}"', 'D', '', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"对下列句中加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"云归而岩穴暝 暝：昏暗\",\"B\":\"杂然而前陈者 陈：摆放\",\"C\":\"太守归而宾客从也 从：服从\",\"D\":\"游人去而禽鸟乐也 去：离开\"}"', 'C', '', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列语句，不属于景物描写的一项是（ ）"', '"{\"A\":\"日出而林霏开\",\"B\":\"野芳发而幽香\",\"C\":\"佳木秀而繁阴\",\"D\":\"四时之景不同\"}"', 'D', '', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列语句，描写宴会场面的一项是（ ）"', '"{\"A\":\"溪深而鱼肥\",\"B\":\"泉香而酒洌\",\"C\":\"射者中，弈者胜\",\"D\":\"人影散乱\"}"', 'C', '', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"对“负者歌于途，行者休于树”的翻译，正确的一项是（ ）"', '"{\"A\":\"自负的人在路上唱歌，僧人在树下休息。\",\"B\":\"自负的人在路上唱歌，走路的人在树下休息。\",\"C\":\"背着东西的人在路上唱歌，僧人在树下休息，\",\"D\":\"背着东西的人在路上唱歌，走路的人在树下休息。\"}"', 'D', '', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2015, '2015年体育单招文化考试', '"下列对于所选文段内容的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"第一段，先写山中早晚光线的明暗变化，再写四季景物的变换，突出了赏景之乐。\",\"B\":\"第二段，写滁人游山，太守宴饮，突出了宾客喧哗、无的放矢、不醉不休的情景。\",\"C\":\"第三段，写游山晚归，突出了太守与民同乐的自得情怀，以及传诸文字的自豪感。\"}"', 'B', '', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2016, '2016年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"与其 参与 与日俱增\",\"B\":\"奢靡 靡费 望风披靡\",\"C\":\"勉强 强迫 强词夺理\",\"D\":\"空闲 空虚 空中楼阁\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2016, '2016年体育单招文化考试', '"下列各组词语中，书写完全正确的一项是（ ）"', '"{\"A\":\"伎两 殊不知 删删来迟\",\"B\":\"期待 全天候 入不敷出\",\"C\":\"寻觅 流览器 按步就班\",\"D\":\"偶像 坐谈会 关怀倍至\"}"', 'C', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2016, '2016年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"调动 接合 从而\",\"B\":\"发动 结合 从而\",\"C\":\"调动 结合 因而\",\"D\":\"发动 接合 因而\"}"', 'D', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2016, '2016年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ )"', '"{\"A\":\"作为曾经的世界冠军，王教练坚信自己的弟子能青出于蓝而胜于蓝。\",\"B\":\"如果把自己获胜的希望寄托在对手的松懈上，必然是竹篮打水一场空。\",\"C\":\"他反应迅速，听风就是雨，教练一个手势，他立刻心领神会。\",\"D\":\"是骡子是马，拉出来遛遛，对嘴把式而言，这应该是最有效的检验。\"}"', 'B', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2016, '2016年体育单招文化考试', '"下列各句中加点成语的使用，恰当的一项是（ )"', '"{\"A\":\"这篇有关比赛的通讯虽然写得相当不错，但由于报道不及时，已是面目全非了。\",\"B\":\"兄弟队频繁地发起肆无忌惮的挑战，这是我们队成员必须面对的严峻事实。\",\"C\":\"今天李义所反映的情况与教练组的观察完全一致，不容置喙，你应该认真面对。\",\"D\":\"很显然，他这一次投出去的标枪歪的太离谱，连裁判都哑然失笑了。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列句子中，有语病的一项是（ )"', '"{\"A\":\"能不能参加这一次的国家队集训，还要看他自己的训练效果够不够好。\",\"B\":\"教练反复交代要就比赛时间、地点及可能出现的问题做好相应的准备。\",\"C\":\"他沉痛地非常宣布，此次外出决赛计划，又因为安全因素的影响而搁浅。\",\"D\":\"没有人会在这样困难的时刻，再提出一些让训练难以为继的要求。\"}"', 'D', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"在横线处填上下列句子和短语，顺序最恰当的一项是（ )"', '"{\"A\":\"③①⑤⑥④②\",\"B\":\"③①④②⑤⑥\",\"C\":\"④②⑤⑥③①\",\"D\":\"④②③①⑤⑥\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下面各句中，没有使用修辞手法的一项（ ）"', '"{\"A\":\"三月的春风，拂动了那湖畔羞涩的杨柳。\",\"B\":\"近看那缀满草尖的水珠，仿佛是数不清的金刚钻。\",\"C\":\"海湾沿岸的灯光，远看去就像蜿蜒在天边的星星。\",\"D\":\"态度端正是实现这次训练目标的关键。\"}"', 'A', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2016, '2016年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"体校斜对面二三十米处有一家小饭馆，学员们经常在那里招待家人。\",\"B\":\"大家都很关心你是选择继续服役提高成绩？还是离开运动队另谋发展？\",\"C\":\"刚刚结束比赛的魏翔一回到运动队，就接到爸爸“有空去看看奶奶”的口信。\",\"D\":\"我们坚持认为：只有真正全身心地投入训练，才能收获满意的成绩。\"}"', 'B', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是（ ）"', '"{\"A\":\"《论语》主要记录了孔子及其弟子的言行。\",\"B\":\"《诗经》是我国第一部诗歌总集。\",\"C\":\"《聊斋志异》的作者是明代文学家蒲松龄。\",\"D\":\"《高老头》的作者是法国作家巴尔扎克。\"}"', 'D', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2016, '2016年体育单招文化考试', '"下列词语中字的读音，不正确的一项是（ ）"', '"{\"A\":\"含蓄（xù)\",\"B\":\"宝藏（cáng)\",\"C\":\"倾慕（qīng)\",\"D\":\"贪婪（lán)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2016, '2016年体育单招文化考试', '"下列词语在文中意思的解释，不正确的一项是（ )"', '"{\"A\":\"震慑：震动恐惧。\",\"B\":\"无可厚非：不可过分指责。\",\"C\":\"盘桓：走来走去。\",\"D\":\"肃然起敬：内心表现出恭敬。\"}"', 'A', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对第1段画线句子的理解和分析，不正确的一项是（ )"', '"{\"A\":\"“拦截”突出了海水中石头对“我”的巨大吸引力。\",\"B\":\"这句话诙谐的写出了作者未能极目远眺时的失望。\",\"C\":\"这句话包含着无名海滩带给作者内心的某种惊喜。\",\"D\":\"这句话写出了作者当时的心境，并自然引出下文。\"}"', 'D', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对第2段中一些词句含意的理解，不正确的一项是（ )"', '"{\"A\":\"从“有的…有的…”这些语句中，可见海水中石头色彩的多样性。\",\"B\":\"“气度不凡地占据了海滩” ，写出了水中的石头带给作者的心理冲击。\",\"C\":\"“犹如贪婪的盗墓者” ，写出了作者对于自己破坏环境的行为的自责。\",\"D\":\"从文意来看，“那些海水中的石头”中的“那些海水中的”是不可缺少的。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对第3、4、5段有关内容的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"“又一批宝贝”带给作者的感受迥异于那些被“首批中选”的石头。\",\"B\":\"“不再美丽”“灰头灰脸”“死亡”是作者对离开大海的石头的感受。\",\"C\":\"作者前后感受不同的原因，主要就在于这些石头所处的时间不同。\",\"D\":\"作者在自己的莽撞中发现，有时候喜爱也会给失误造成“伤害”。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"文中提到科学家法布尔，下列作品属于法布尔作品的一项是（ )"', '"{\"A\":\"《物种起源》\",\"B\":\"《昆虫记》\",\"C\":\"《海底两万里》\",\"D\":\"《自然史》\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对文章的理解和分析，不正确的一项是（ )"', '"{\"A\":\"文章借石头“生”与“死”的对比，突出了“我”认识上的转变。\",\"B\":\"文章批评了那种因为爱或喜欢就想占有的心理习惯及行为方式。\",\"C\":\"让园子里的石头像那海中的石头一样存在，是法布尔建立荒石园的原因。\",\"D\":\"作者在文中也委婉提出，人类应该尊重事物的自然存在状态。\"}"', 'A', '', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是（ ）"', '"{\"A\":\"海边荒石\",\"B\":\"那海滩的石头\",\"C\":\"海边石头\",\"D\":\"我的石头记忆\"}"', 'B', '', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2016, '2016年体育单招文化考试', '"下列各句中加点字的读音，不正确的一项是（ )"', '"{\"A\":\"辍耕之陇上 辍：chuò\",\"B\":\"燕雀安知鸿鹄之志哉 鹄：hú\",\"C\":\"道不通，度已失期 度：duó\",\"D\":\"今诚以吾众诈自称公子扶苏 诈：zuò\"}"', 'C', '', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列各句中，加点的字是通假字的一项是（ ）"', '"{\"A\":\"尝与人佣耕\",\"B\":\"九百人屯大泽乡\",\"C\":\"扶苏以数谏故\",\"D\":\"为天下唱，宜多应者\"}"', 'D', '', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列句中加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"苟富贵，无相忘 苟：如果。\",\"B\":\"会天大雨，道不通 会：适逢。\",\"C\":\"爱士卒，楚人怜之 怜：爱戴。\",\"D\":\"或以为死，或以为亡 亡：死亡。\"}"', 'C', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.101 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列句子中，是判断句的一项是（ )"', '"{\"A\":\"陈胜者，阳城人也。\",\"B\":\"等死，死国可乎？\",\"C\":\"上使外将兵。\",\"D\":\"百份多闻其贤，未知其死也。\"}"', 'C', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.106 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列文句的翻译，不正确的一项是（ )"', '"{\"A\":\"若为佣耕，何富贵也？ ——你是一个替人耕田的人，怎么会富贵起来呢？\",\"B\":\"发闾左适戍渔阳。 ——征发贫苦人民去驻守渔阳。\",\"C\":\"天下苦秦久矣。 ——天下百姓生活艰苦，秦统治者知道这一点已很久了。\",\"D\":\"吴广以为然。 ——吴广认为（这个见解）很正确。\"}"', 'D', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.112 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2016, '2016年体育单招文化考试', '"下列对所选文段内容的理解和分析，不正确的一项是（ )"', '"{\"A\":\"第一段主要介绍陈胜年轻时的情况，“尝与人佣耕”显示了他当时的境况。\",\"B\":\"“燕雀安知鸿鹄之志” 表明了陈胜、吴广两人年轻时就有远大的抱负。\",\"C\":\"陈胜、吴广起义的地点是 “大泽乡” ， “失期，法皆斩” 是起义的导火线。\",\"D\":\"“诈自称公子扶苏、项燕” 的策略，显示出陈胜在决策时的过人胆识。\"}"', 'C', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.118 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2017, '2017年体育单招文化考试', '"下列各组词语中加粗的字，读音全都相同的一项是（ ）"', '"{\"A\":\"埋伏 埋怨 埋头苦干\",\"B\":\"着落 落魄 丢三落四\",\"C\":\"折叠 折扣 令人折服\",\"D\":\"宿命 星宿 体坛宿将\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列各组词语中，书写完全正确的一项是（ ）"', '"{\"A\":\"座落 虚荣心 目不交捷\",\"B\":\"漫骂 随大流 义气用事\",\"C\":\"融恰 算总账 直言不诲\",\"D\":\"蕴藏 烂摊子 辗转反侧\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下面文段中加粗的词语，能够用括号中词语替代的一项是（ ）"', '"{\"A\":\"一旦（如果）\",\"B\":\"就（也）\",\"C\":\"就（才）\",\"D\":\"从而（所以）\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2017, '2017年体育单招文化考试', '"下列各句中加粗熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"拜仁慕尼黑足球队来访，北京球迷， 近水楼台先得月 有机会到现场看比赛。\",\"B\":\"这两支球队曾有20次比赛记录，结果是两队各赢10场，真可谓 旗鼓相当 。\",\"C\":\"针对他的新年微博秀，一些网友认为他应该专心训练，而不是急着 唱高调 。\",\"D\":\"他在省队训练的时间不长，有人就认为他在那里不过是一个 泡蘑菇 的角色。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2017, '2017年体育单招文化考试', '"下列各句中加粗成语的使用，恰当的一项是（ )"', '"{\"A\":\"假如每个人都能 见贤思齐 ，以人之长补己之短，那么我们每天都会有进步。\",\"B\":\"不管他自己怎么认为，对于职业联赛而言，他都只是一个 不名一文 的小人物。\",\"C\":\"遵照相关法律和要求，各行各业中 上行下效 ，社会风气就会逐步好转起来。\",\"D\":\"联赛环境得以改善，地方政府也 推波助澜 ，成就了这一次主场的巨大胜利。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列句子中，有语病的一项是（ )"', '"{\"A\":\"到底需要不需要重新比赛，取决于仲裁机构最后做出什么样的裁决。\",\"B\":\"他一声不吭，赶紧递过去一块毛巾，心里暗暗期待队友能够长期坚持下去。\",\"C\":\"对运动员而言，汗水、比赛服以及比赛场地，全都是值得回忆的美好事物。\",\"D\":\"从教练组出来时，我们看得出来，他已经摆脱完全从前的那种精神面貌了。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ )"', '"{\"A\":\"④③②⑤①\",\"B\":\"③①④⑤②\",\"C\":\"④②③①⑤\",\"D\":\"③⑤④①②\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列各句中，没有使用修辞手法的一项是（ )"', '"{\"A\":\"谁都明白，教练就是教练，绝对不能搞得不像个教练。\",\"B\":\"白发三千丈，缘愁似个长。不知明镜里，何处得秋霜。\",\"C\":\"大家正犹豫，就看见有一个红领巾跑上前去扶起了她。\",\"D\":\"发令枪刚一响，各位选手就像离弦的箭一样冲了出去。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2017, '2017年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ )"', '"{\"A\":\"小时候我就在村里的小学上学，学校离家不过二三里地，还是很方便的。\",\"B\":\"我们面对的问题是，有些运动员不想付出努力，整天就等着天上掉馅饼？\",\"C\":\"离开训练队很久了，但是“要做就做出个样子来”的声音仿佛还在耳边。\",\"D\":\"这一次比赛我们又大获全胜：兵乓球各项目的冠军再一次为我们包揽了。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列有关作家作品的表述，不正确的是（ ）"', '"{\"A\":\"《爱莲说》的作者是周敦颐。\",\"B\":\"《三国演义》的作者是罗贯中。\",\"C\":\"《平凡的世界》的作者是路遥。\",\"D\":\"《项链》的作者是法国福楼拜。\"}"', 'B', '成就大事的人，大都有一种“坐忘”的功夫。《庄子》里讲了许多“凝神”的故事，“佝偻承蜩”就是其中之一。故事中写的是一个身材佝偻的老人，用竹竿捕蝉，就像用手拾取一枚落叶一样轻而易举。为什么能够这样轻松呢？老人说我是经过刻苦训练的。我在竹竿上叠加一些小泥丸，先是叠加两颗，接着三颗，最后五颗。当伸出竹竿捕蝉时，竹竿头上的五颗泥丸一颗也不掉下来，说明我的稳定性已经绝佳。到那时候，再用竹竿捕蝉，就好像是用手去捡一样，万无一失。而且，我的身子站定在那，就像没有知觉的断木桩子；我举着的手臂，就像枯树枝；即使天地很大，物类繁盛，可我只知道有蝉翼在眼前。我不回头不侧身，不因万物而改变对蝉翼的注意，还有什么蝉捕不到？庄子用这个故事，告诉了我们“用志不分，乃凝于神”的道理。 “坐忘”是一种崇高的人生境界。人生苦短，应该把主要精力用在有价值的事情上，不要受一些外在事物的干扰。不要在外在的名利上过于计较，做一件事，只要觉得它于世道人心有价值，就应该专心去做，至于结果如何，自己能不能从这件事上得到什么好处，那都不是自己该考量的。有了这样坦然的心态，为人就不会患得患失，做事就可以善始善终，倘若真能够百折不回，也就有可能做出一些大事来。 “坐忘”的精神，就是要执着一念，滴水穿石。光阴不是用来消磨的，是用来开花结果的。“坐忘”的人，就是用心浇灌光阴这棵美丽的树，等到树上开花结果。如果无视光阴这颗美丽的树，不去经营它，任凭它枯萎掉，那样的人生，该是多么失败！ 11. 下列词语中加粗的字读音，不正确的一项是（ ）', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2017, '2017年体育单招文化考试', '"下列词语中加粗的字读音，不正确的一项是（ ）"', '"{\"A\":\"浮躁（zào)\",\"B\":\"干扰（rǎo)\",\"C\":\"执着（zháo)\",\"D\":\"枯萎（wěi)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列词语中加粗字的解释，不正确的一项是（ ）"', '"{\"A\":\"心无旁骛（追求）\",\"B\":\"掷去之（抛掷）\",\"C\":\"佝偻承蜩（拾取）\",\"D\":\"凝于神（盯着）\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列词语在文中的意思，解释不正确的一项是（ ）"', '"{\"A\":\"魂不守舍：指做事情时精神不能集中。\",\"B\":\"判若云泥：形容高低差异很难去判断。\",\"C\":\"轻而易举：形容做事情很容易就成功。\",\"D\":\"患得患失：一般指为人处世顾虑重重。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('reading', 2017, '2017年体育单招文化考试', '"阅读文章第三段，下列理解或分析不正确的一项是（ )"', '"{\"A\":\"这一段在讲管宁和华歆的交往时，提到了他们生活中的两个小故事。\",\"B\":\"从本文介绍看，“金子”和“车马”对管宁和华歆的影响截然不同。\",\"C\":\"管宁选择与华歆割席绝交，是因为他不愿意接受华歆的财物和身份。\",\"D\":\"管宁和华歆的区别，其实在于能不能够专心对待自己正在做的事情。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('reading', 2017, '2017年体育单招文化考试', '"阅读文章第四段，下列理解或分析正确的一项是（ )"', '"{\"A\":\"文章这一段写的是成就大事的人，与第三段写管宁华歆的生活小事不相同。\",\"B\":\"佝偻老人捕蝉可以万无一失，原因就在于他用刻苦训练弥补了身体的缺陷。\",\"C\":\"文章写佝偻老人的捕蝉训练过程，表明技艺进步永远都没有止境这个道理。\",\"D\":\"佝偻老人的成功，既在于有技术，也在于他有一种忘我的精神境界与追求。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"文中提到《庄子》和《世说新语》，相关常识表述有误的一项是（ )"', '"{\"A\":\"文章借《庄子》的“坐忘”起笔，明确自己对“坐忘”的不同看法。\",\"B\":\"文章对比解释“坐忘”“坐驰”，并引《世说新语》中的故事来说明。\",\"C\":\"文章用“佝偻承蜩”老人的超凡技能，来突出“坐忘”的崇高境界。\",\"D\":\"文章希望大家都能够有“坐忘”这样的境界，去做一些有价值的事。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列词语中，最适合做文章标题的一项是（ )"', '"{\"A\":\"“坐忘”小议\",\"B\":\"坐忘\",\"C\":\"“坐忘”与“坐驰”\",\"D\":\"凝神\"}"', 'B', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.125 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2017, '2017年体育单招文化考试', '"下列各句中加粗字的读音，不正确的一项是（ )"', '"{\"A\":\"初级狭，才通人 狭：jiá\",\"B\":\"土地平旷，屋舍俨然 俨：yǎn\",\"C\":\"便要还家，设酒杀鸡作食。 要：yāo\",\"D\":\"率妻子邑人来此绝境。 率：shuài\"}"', 'D', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.132 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列句子加粗的词的解释，不正确的一项是（ ）"', '"{\"A\":\"复前行，欲穷其林 穷：走到头\",\"B\":\"有良田美池桑竹之属 属：一类\",\"C\":\"余人各复延至其家 延：拖延\",\"D\":\"便扶向路，处处志之 志：做标记\"}"', 'C', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.137 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2017, '2017年体育单招文化考试', '"下列句中加粗的词语，与现代汉语意思不同的一项是（ ）"', '"{\"A\":\"缘溪行，忘路之远近\",\"B\":\"仿佛若有光\",\"C\":\"阡陌交通，鸡犬相闻\",\"D\":\"乃不知有汉，无论魏晋\"}"', 'B', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.142 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2017, '2017年体育单招文化考试', '"下列选项中，全都是成语的一项是（ ）"', '"{\"A\":\"①②③④\",\"B\":\"①②③⑤\",\"C\":\"①②④⑤\",\"D\":\"②③④⑤\"}"', 'C', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.148 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列文句的翻译，不正确的一项是（ ）"', '"{\"A\":\"渔人甚异之－渔人，觉得非常奇异\",\"B\":\"便舍船，从口入－于是停下船，从山上的小口进入\",\"C\":\"不复出焉，遂与外人间隔－－不再从这里出去就和人间分隔开来，\",\"D\":\"不足为外人道也－不值得向外面的人说（在这里的情况）\"}"', 'D', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.152 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2017, '2017年体育单招文化考试', '"下列对所选文段内容的理解和分析不正确的一项（ ）"', '"{\"A\":\"桃花源的发现得益于一个渔人一次偶然的发现和探寻。\",\"B\":\"桃花源的环境非常美好，人们在这里过着幸福的生活。\",\"C\":\"桃花源人以耕作为业，对还有渔人这样的人感到吃惊。\",\"D\":\"桃花源中的居民并不希望自己平静的生活被打扰。\"}"', 'A', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.156 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2018, '2018年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"假话 假设 假公济私\",\"B\":\"计划 划船 划清界限\",\"C\":\"凶恶 恶心 深恶痛绝\",\"D\":\"内蒙 蒙骗 蒙头转向\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"下列各组词语中，书写完全正确的一项是（ ）"', '"{\"A\":\"等候 坐上客 鞠躬尽瘁\",\"B\":\"奢华 牛角尖 轻重缓急\",\"C\":\"少许 历程碑 居安思危\",\"D\":\"稍息 居留权 礼上往来\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"表现 然而 即使\",\"B\":\"表现 那么 如果\",\"C\":\"出现 但是 如果\",\"D\":\"出现 不过 即使\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2018, '2018年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"她边走边笑道：“你贵人多忘事，哪里还记得我们这些小百姓呢。”\",\"B\":\"果然是人靠衣裳马靠鞍，她这么一打扮，简直跟换了个人似的。\",\"C\":\"这件事八字没一撇呢，你先别着急啊，咱们有的是时间来应对。\",\"D\":\"他们俩不打不相识，自此变成了仇人，互相争斗怨恨了一辈子。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2018, '2018年体育单招文化考试', '"下列各句中加点成语的使用，恰当的一项是（ ）"', '"{\"A\":\"五月的黄山是最美的，风景如画，气候宜人，前来旅游的人不绝如缕。\",\"B\":\"谈起黄梅戏，这个年仅九岁的孩子竟然说得头头是道，让人惊叹不已。\",\"C\":\"对孩子进行爱国教育不仅是学校的工作，更是每个家长任劳任怨的责任。\",\"D\":\"这次物理竞赛，以前成绩平平的小明竟然得了一等奖，真让人另眼相看。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列句子中，没有语病的一项是（ ）"', '"{\"A\":\"不管天气极其炎热，环卫工人为了大家有个干净舒适的环境，仍然坚持工作。\",\"B\":\"参观了这次科技展以后，使我们了解到了我们国家科技发展取得的巨大成就。\",\"C\":\"我们要努力构建体育宣传新格局，为体育事业改革发展营造良好的舆论氛围。\",\"D\":\"兴办孔子学院让世界不仅更加了解中国，而且激发了外国友人学汉语的热情。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ ）"', '"{\"A\":\"③①⑤⑥④②\",\"B\":\"③①④②⑤⑥\",\"C\":\"④②⑤⑥③①\",\"D\":\"④②③①⑤⑥\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列各句中，没有使用修辞手法的一项是（ ）"', '"{\"A\":\"小屋的出现，点破了山的寂寞，增加了风景的内容。\",\"B\":\"雌凤蝶背上，是两对绣着黄色花纹的黑绒似的翅膀。\",\"C\":\"她每晚在楼上挂一盏明灯，为前来相会的爱人引路。\",\"D\":\"月光如流水一般，静静的泻在这一片叶子和花上。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2018, '2018年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"亚洲地跨寒、温、热三带，又因各地地形和距离海洋远近不同，气候复杂多样。\",\"B\":\"张华考上了北京大学，在化学系学习；李萍考进中等技术学校，读机械制造专业。\",\"C\":\"现代画家徐悲鸿笔下的马，正如有的评论家说的那样，“神形兼备，充满生机”。\",\"D\":\"读完课文，他站起来走到老师面前问道：“老师，“有条不紊”的紊是什么意思？”。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列有关作家作品的表述，不正确的是（ ）"', '"{\"A\":\"《兰亭序》是唐代著名书法家王羲之所作。\",\"B\":\"《孟子》记载了战国思时期想家孟轲的言行。\",\"C\":\"《荷塘月色》是朱自清写的一篇抒情性散文。\",\"D\":\"《巴黎圣母院》是法国作家雨果的长篇小说。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2018, '2018年体育单招文化考试', '"下列词语中加点的字读音，不正确的一项是（ )"', '"{\"A\":\"震惊（zhèn)\",\"B\":\"摩挲（suō)\",\"C\":\"抽屉（shi)\",\"D\":\"门槛（kǎn)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"下列对文中词语的解释，不正确的一项是（ )"', '"{\"A\":\"瞥见：长久注视\",\"B\":\"赫然：引人关注\",\"C\":\"交叠：交错重合\",\"D\":\"朴素：简单朴素\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对第1段和第2段有关内容的理解和分析，不正确的一项是（ )"', '"{\"A\":\"作者感到震惊的原因是他没想到缝纫机与三十多年前一样新。\",\"B\":\"母亲专注车裤子的神情让作者回忆起了童年的点点滴滴。\",\"C\":\"第2段起作者开始回忆童年，并借此展现了母亲的勤劳。\",\"D\":\"作者通过对写功课这一件事的描述，写出了贫困生活的乐趣。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对第2段和第3段中一些句子含意的理解，不正确的一项是（ )"', '"{\"A\":\"“门外就是爸爸种的一大片枣子园”自然带出下文给母亲摘枣子的内容。\",\"B\":\"“她做的是把面粉袋、肥料袋车成简单的服装”，表现了母亲的心灵手巧。\",\"C\":\"“疤痕，如今还清晰可见”写出了作者对哥哥仍然颇有微词。\",\"D\":\"“心里却是无所畏惧的”，表现出母亲在生活中的坚强感染到了作者。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对第4段有关内容的理解和分析，不正确的一项是（ )"', '"{\"A\":\"每天早上，太阳出来以后，母亲就开始准备工作了。\",\"B\":\"光线打在母亲侧脸上显现出的色彩与线条，让作者的印象尤为深刻。\",\"C\":\"家中的孩子们对母亲的辛劳都看在眼中，并争相摘枣子敬献给母亲。\",\"D\":\"母亲用自己的辛勤劳作让孩子们从贫苦的生活中感受到无边的幸福。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"文中第2段画线处的词语，填写恰当的一项是（ )"', '"{\"A\":\"由于 因此\",\"B\":\"虽然 也不至于\",\"C\":\"虽然 尚未\",\"D\":\"即使 但不曾有\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对文章的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"文章借助缝纫机这一实物，由现在推及过去，表现了母亲的辛勤劳作。\",\"B\":\"文章的语言简单、质朴，辞藻虽不华美，情感却令人动容。\",\"C\":\"作者运用细节描写来追忆童年生活，凸显了“母爱”这一主题。\",\"D\":\"文章结尾用寥寥数语表现出作者对母亲衰老，时光易逝的感慨。\"}"', 'B', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.161 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2018, '2018年体育单招文化考试', '"下列词语中，最适合做文章标题的一项是（ )"', '"{\"A\":\"妈妈的回忆\",\"B\":\"透早的枣子园\",\"C\":\"缝纫机轶事\",\"D\":\"我童年的记忆\"}"', 'A', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.165 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2018, '2018年体育单招文化考试', '"下列各句中加点字的读音，不正确的一项（ ）"', '"{\"A\":\"秦军汜南 汜 fán\",\"B\":\"夜缒而出 缒 zhui\",\"C\":\"许君焦瑕 瑕xiá\",\"D\":\"杨孙戍之 戍xù\"}"', 'A', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.170 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列句中加点词的解释，不正确的是（ ）"', '"{\"A\":\"晋军函陵 军：驻军\",\"B\":\"犹不如人 犹：尚且\",\"C\":\"郑既知亡矣 既：已经\",\"D\":\"又欲肆其西封 肆：放肆\"}"', 'D', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.174 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列句中，“以”字的意义不同于其他三项的是（ ）"', '"{\"A\":\"以其无礼于晋\",\"B\":\"越国以鄙远\",\"C\":\"焉用亡郑以陪邻\",\"D\":\"阙秦以利晋，唯君图之\"}"', 'D', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.178 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列文句的翻译，不正确的一项是（ ）"', '"{\"A\":\"贰于楚也 ——从属于晋的同时又从属于楚\",\"B\":\"无能为也已 ——不能干什么了\",\"C\":\"尝与晋君赐矣 ——曾经被晋国国君赏赐\",\"D\":\"将焉取之 ——将从哪里取得他所贪求的土地呢\"}"', 'B', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.183 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对所选文段内容的理解和分析，不正确的一项是（"', '"{\"A\":\"第一段交代了秦晋两国攻打郑国的原因。\",\"B\":\"在烛之武壮年时，郑伯就曾请他参政议政。\",\"C\":\"晋国曾经答应给秦国土地，却没有兑现。\",\"D\":\"最终，秦国单方面与郑国结盟，撤军而回。\"}"', 'C', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.187 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2018, '2018年体育单招文化考试', '"下列对文中画波浪线部分的断句，正确的一项是（ ）"', '"{\"A\":\"吾不能早用／子今急而求子／是寡人之过也／然郑亡／子亦有不利焉。\",\"B\":\"吾不能早用子／今急而求子／是寡人之过也／然郑亡子／亦有不利焉。\",\"C\":\"吾不能早用／子今急而求子／是寡人之过也然郑亡／子亦有不利焉。\",\"D\":\"吾不能早用子／今急而求子／是寡人之过也／然郑亡／子亦有不利焉。\"}"', 'D', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.192 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2019, '2019年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全都相同的一项是（ ） [cite: 572]"', '"{\"A\":\"卷曲 席卷 开卷有益\",\"B\":\"数秒 历数 数九寒天\",\"C\":\"度量 弧度 审时度势\",\"D\":\"难堪 责难 难能可贵\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2019, '2019年体育单招文化考试', '"[cite_start]下列各组词语中，书写完全正确的一项是（ ) [cite: 577]"', '"{\"A\":\"荣膺 凝聚力 再接再厉\",\"B\":\"精湛 充其量 耐人询味\",\"C\":\"俯瞰 抱冷门 空谷足音\",\"D\":\"酬划 抚恤金 墨守成规\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2019, '2019年体育单招文化考试', '"[cite_start]依次填入下面一段文字横线处的词语，最恰当的一项是（ ) [cite: 582]"', '"{\"A\":\"传达 表扬 反而\",\"B\":\"传播 表彰 反而\",\"C\":\"传播 表彰 进而\",\"D\":\"传达 表扬 进而\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2019, '2019年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用，不正确的一项是（ ) [cite: 588]"', '"{\"A\":\"彼一时，此一时，现在训练条件好了，我们应更珍惜每一次训练，提高成绩。\",\"B\":\"如果训练出了问题，我们一定要抓住机会，头痛医头，脚痛医脚，及时解决。\",\"C\":\"他高考成绩没有预期的好，这让他在填报志愿时高不成，低不就，左右为难。\",\"D\":\"摩托车赛手在比赛中摔倒，虽然有惊无险，但当时还是让观众为他捏一把汗。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2019, '2019年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ) [cite: 593]"', '"{\"A\":\"如果在训练时不刻苦不认真，心猿意马，那么在正式比赛中就很难取得好的成绩。\",\"B\":\"有关部门统筹了各方面的调研实践，集思广益，最终制定了这项运动的指导纲要。\",\"C\":\"现阶段，体育产业进入了高速发展时期，群众对体育产品的接受度可谓基器尘上。\",\"D\":\"竞技体育、学校体育与群众体育，三者应该相互协调、相辅相成，不能有所偏颇。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 598]"', '"{\"A\":\"赛会申办能否成功，主要取决于申办城市基础设施的建设是不是完善。\",\"B\":\"作为鄂温克族最具特色的传统体育项目，“抢枢”运动已有千年历史。\",\"C\":\"完善而又强有力的冰雪运动管理体制是冰雪运动健康发展的有力保证。\",\"D\":\"从长远看，全国青少年校园足球活动是一个需要常抓不懈，不能放松。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]在横线处填入下列句子或短语，顺序最恰当的一项是（ ) [cite: 603]"', '"{\"A\":\"①②⑤③⑥④\",\"B\":\"②①③④⑥⑤\",\"C\":\"②①⑤④③⑥\",\"D\":\"①②④③⑤⑥\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]下面各句中使用的修辞手法，与其他三项不同的一项是（ ) [cite: 613]"', '"{\"A\":\"这株高大的冬樱花，一到新年，便盛放如一蓬温柔的火焰。\",\"B\":\"河水一段儿清泠泠，一段儿喧哗不已，在向我们大声吵嚷。\",\"C\":\"到了黄昏时分，麦草已经像小山一样地堆在静寂的麦场上。\",\"D\":\"初秋，蒲公英的花絮飞满天空，就像一朵朵洁白的降落伞。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2019, '2019年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ) [cite: 618]"', '"{\"A\":\"“南国书城” 《天一阁》，是我国现存最早的一座私家藏书楼。\",\"B\":\"一个骑着摩托车的年轻人在我身边停下，问我：你要找哪家？\",\"C\":\"两三丈外，只见一片烟云－仍遮抹一切，只不过是雾样罢了。\",\"D\":\"推开窗。雨，密密匝匝地在树上响作一团，一滴滴敲响树叶。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"[cite_start]下列有关作家作品的表述，不正确的一项是（ ) [cite: 623]"', '"{\"A\":\"《离骚》是战国时代楚国屈原的抒情诗。\",\"B\":\"《西厢记》的作者是明代戏剧家王实甫。\",\"C\":\"《边城》是中国现代作家沈从文的小说。\",\"D\":\"《老人与海》的作者是美国作家海明威。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2019, '2019年体育单招文化考试', '"下列词语中加点的字的读音，不正确的一项是（ )"', '"{\"A\":\"承载（zài)\",\"B\":\"储存（zhù)\",\"C\":\"摇曳（yè)\",\"D\":\"掺杂（chān)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2019, '2019年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ )"', '"{\"A\":\"融合：合为一体。\",\"B\":\"货真价实：货不假，价实在。\",\"C\":\"肆意：竭尽全力。\",\"D\":\"小心翼翼：举动谨慎，不敢疏忽\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对第1段三个“忍不住”语意指向的理解不正确的一项是（ ）"', '"{\"A\":\"第一个“忍不住”表现了超市里粮食对作者的吸引力。\",\"B\":\"第二个“忍不住”表现了作者亲近粮食的渴望和行为。\",\"C\":\"第三个“忍不住”表现了作者面对粮食产生无尽感慨。\",\"D\":\"三个“忍不住”都表现出作者对人们漠视粮食的伤感。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对第2段中画线句子的理解和分析，正确的一项是（ )"', '"{\"A\":\"“花花绿绿”，是指超市里面那些吸引消费者的好看而又抢眼的商品。\",\"B\":\"“这个过程”，是指粮食从生产、加工、运输到进入超市的整个过程。\",\"C\":\"这两句表明，人们购物匆匆，没有时间辨析比较粮食的品质与价格。\",\"D\":\"这两句表明，超市信誉较好，粮食价格又便宜，不用挑选就可以买。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对文中有关内容的理解和分析，正确的一项是（ )"', '"{\"A\":\"“装作很饥饿的样子”，是因为“我”要给孩子做出榜样，让他们热爱粮食。\",\"B\":\"“只吃过几次就失去了资格”，言外之意是家里边又有了更小的弟弟或妹妹。\",\"C\":\"“那炊烟里总是带着粮食的香气”，写出作者在那个饥饿年代对米饭的渴望。\",\"D\":\"母亲“挑几个好看的”地瓜给我们当饭吃，是为了让我们忘记地瓜是猪食。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对粮食变味的原因的理解，不正确的一项是（ )"', '"{\"A\":\"现在人们生活水平提高了，品尝到的美味多了。\",\"B\":\"现在人们生活节奏加快了，对事物麻木不仁了。\",\"C\":\"现在的粮食缺少自然洗礼、亲人的劳动和情感。\",\"D\":\"现在的粮食从超市里买来，商品化气息太浓厚。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对文章的理解和分析，不正确的一项是 ( )"', '"{\"B\":\"文章从今昔对比的角度分析问题，因而更加突显了反思精神和失落情怀。\",\"C\":\"文章在议论、抒情中穿插叙述、描写，使得议论和抒情都显得有的放矢。\",\"D\":\"文章的首尾两段遥相呼应，都从珍惜的角度融入思考，从而深化了主题。\"}"', 'B', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.198 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是 ( )"', '"{\"A\":\"粮食的滋味\",\"B\":\"粮食的味道\",\"C\":\"粮食的气息\",\"D\":\"粮食的韵律\"}"', 'D', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.202 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2019, '2019年体育单招文化考试', '"下列句子中加点的字的读音，完全正确的一项是（ )"', '"{\"A\":\"①dù ②bù ③yú\",\"B\":\"①dú ②fǒu ③chàn\",\"C\":\"①dòu ②bù ③chàn\",\"D\":\"①dòu ②fǒu ③yú\"}"', 'A', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.206 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列依次对句子中的“师”字的解释，正确的一项是（ )"', '"{\"A\":\"学习 老师 从师 老师\",\"B\":\"老师 老师 学习 从师\",\"C\":\"老师 从师 学习 老师\",\"D\":\"学习 从师 老师 老师\"}"', 'B', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.211 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列句子中加点的字，与“于其身也，则耻师焉”中的“耻”字，用法不相同的一项（)"', '"{\"A\":\"成以其小，劣之\",\"B\":\"登泰山而小天下\",\"C\":\"吾妻之美我者，私我也\",\"D\":\"焚百家之言，以愚黔首\"}"', 'D', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.216 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列每组人物，在“从师态度”上没有形成对比的一项是（ ）"', '"{\"A\":\"圣人与众人\",\"B\":\"圣人与愚人\",\"C\":\"巫医乐师百工之人与士大夫\",\"D\":\"童子之师与传道解惑者\"}"', 'C', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.221 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"对文中所说的“传其道解其惑”中的“道”，理解正确的一项是（ )"', '"{\"A\":\"一般人都应该懂得的大道理\",\"B\":\"主要指封建社会的道德标准\",\"C\":\"儒家经典中体现出来的儒家学说\",\"D\":\"古人对于宇宙运行规律的一种认识\"}"', 'B', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.225 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2019, '2019年体育单招文化考试', '"下列对文段内容的理解与分析，不正确的一项是（ )"', '"{\"A\":\"文段连用对比，突出了要改变社会中耻于相师这一风气的紧迫性。\",\"B\":\"文段通过多个例证，反映了社会中耻于相师这一现象的普遍性。\",\"D\":\"文段陈述了师道不复的不良风气，进而表达了自己的不满和担忧。\"}"', 'B', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.230 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2020, '2020年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全部相同的一项是（ ） [cite: 895]"', '"{\"A\":\"竞相 相左 相提并论\",\"B\":\"几许 茶几 几次三番\",\"C\":\"角逐 角色 勾心斗角\",\"D\":\"供职 供养 供不应求\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"[cite_start]下列各组词语，书写完全正确的一项是（ ） [cite: 900]"', '"{\"A\":\"请缨 赶时髦 火中取粟\",\"B\":\"疲蔽 连轴转 管中窥豹\",\"C\":\"集锦 合订本 凤毛麟角\",\"D\":\"誊清 大杂会 老骥伏枥\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"[cite_start]依次填入下面一段文字横线处的词语，最恰当的一项是（ ） [cite: 905]"', '"{\"A\":\"基准 演示 随着\",\"B\":\"基础 演变 随着\",\"C\":\"基准 演变 随同\",\"D\":\"基础 演示 随同\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2020, '2020年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用， 不正确 的一项是（ ) [cite: 911]"', '"{\"A\":\"在游艺比赛中，体育生占尽优势，不费吹灰之力，迅速闯关，率先到达终点。\",\"B\":\"作为一代拳王，他在巅峰时期号称 “不败王者” ，三下五除二就击倒了对手。\",\"C\":\"记者获悉，这家俱乐部引进外籍球员的事还在报价阶段，加盟八字没一撇呢。\",\"D\":\"为了保证比赛的公平性，八分之一决赛唱对台戏，四个队两场比赛同时进行。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2020, '2020年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ) [cite: 916]"', '"{\"A\":\"这支球队新赛季主力阵容终于亮相，未来成绩如何，众说纷纭，见仁见智。\",\"B\":\"发令枪一响，运动员们就像离弦的箭一样飞快冲出去，竞争场面触目惊心。\",\"C\":\"在农民趣味运动会中， “赶着大鹅奔小康” 的比赛别开生面，格外引人注目。\",\"D\":\"体育营销要以心理效应为主，长期努力才能实现各种收益，不能一蹴而就。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 921]"', '"{\"A\":\"他表示，将在隔离的14天里进行室内训练，然后回到球场，打好每一场比赛。\",\"B\":\"英超联赛如果跟随政府的防疫思路，本赛季、甚至下个赛季的比赛都会受影响。\",\"C\":\"为期三天的展会，将展演各种有地方特色的传统体育活动，展现传统体育魅力。\",\"D\":\"体育所包含的价值观念、精神状态以及文化认知等，一定都会随着时代的变化。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]在横线处填入下列句子或短语，顺序最恰当的一项是（ ） [cite: 926]"', '"{\"A\":\"⑤②③①④\",\"B\":\"⑤③①④②\",\"C\":\"③①②⑤④\",\"D\":\"③②①④⑤\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]下面各句中使用的修辞手法，与其他三项不同的一项是（ ） [cite: 937]"', '"{\"A\":\"洱海，这面光洁的梳妆镜。南北长百里，东西宽十余里。\",\"B\":\"香溪如歌如泣地前行，越看越感到亲切并喜欢它的风格。\",\"C\":\"远远地看见一座高塔耸立在山坡上，许多绿树拥抱着它。\",\"D\":\"车在奔驰，风在欢笑，成熟的晚稻，沉沉地压在大地上。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2020, '2020年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ） [cite: 942]"', '"{\"A\":\"孔子的学生把他的遗物——衣、冠、琴、车、书－－保存在他的故居，作为 “庙” 。\",\"B\":\"加缪说： “人们可以在几种意义上阅读《鼠疫》，它最明显的意义就是抵抗纳粹。”\",\"C\":\"“牛最喜欢吃这些茅草，你留着反正也是没有用的……” 他喃喃地说，赶牛走了。\",\"D\":\"你本该属于蓝天，却亿万年躺在地上，被苦涩腌渍。我该向你说什么？青海湖？\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"[cite_start]下列有关作家作品的表述， 不正确 的一项是（ ） [cite: 947]"', '"{\"A\":\"《桃花源记》是汉末陶渊明的散文。\",\"B\":\"《醉翁亭记》是北宋欧阳修的散文。\",\"C\":\"《女神》是现代作家郭沫若的诗集。\",\"D\":\"《复话》的作者是列夫·托尔斯泰。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2020, '2020年体育单招文化考试', '"下列词语中加点字的读音，不正确的一项是（ )"', '"{\"A\":\"木屐（jī)\",\"B\":\"脍炙人口（zhì)\",\"C\":\"嵌进（qiàn)\",\"D\":\"鳞次栉比（jié)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ )"', '"{\"A\":\"通衢：四通八达的道路。\",\"B\":\"引人入胜：让人引以为豪。\",\"C\":\"联袂：这里是“相连”之意。\",\"D\":\"沧海桑田：形容世事巨变。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"第4段写到父亲决定带我去街上看月。下列有关理解和分析，不正确（ )"', '"{\"A\":\"“我”的喊声引发父亲看月的好奇心，唤醒他美好的记忆。\",\"B\":\"“我”的喊声让父亲突然想起下街头才是看月的绝佳之地。\",\"C\":\"父亲“忽然想起什么似的”，表明他是临时决定去看月的。\",\"D\":\"父亲“立即拉着我下楼”，表现出他亲切果决的性格特点\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对第6段中“那练潭秋月最不同凡响”的理解和分析，正确的一项（ )"', '"{\"A\":\"“练潭秋月”，远处有山影，近处有苇、荷，风景宜人，所以不同凡响。\",\"B\":\"“练潭秋月”，为“桐城八景“之，因为与父亲同游，所以不同凡响。\",\"C\":\"“练潭秋月”，有天上月，水中月，还有弯月形的水潭，所以不同凡响。\",\"D\":\"“练潭秋月”让我痴迷留恋，生出如真似幻般的感觉，所以不同凡响。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对第7段中“竟磨灭了那月的灵性”的理解，正确的一项是（ ）"', '"{\"A\":\"“那月” ，指天上的月亮； “磨灭灵性” ，指云遮雾掩。\",\"B\":\"“那月” ，指水中的月亮； “磨灭灵性” ，指模糊不清。\",\"C\":\"“那月” ，指心中的月亮， “磨灭灵性” ，指记忆续薄。\",\"D\":\"“那月” ，指潭水的形状， “磨灭灵性” ，指湖水变窄。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"文中写“我”三次到练潭，下列有关概据分析。不正确的一项是（ ）"', '"{\"A\":\"童年时代到练潭，老街繁华，月色美好，湖水清澈，铭刻在记忆里永远不忘怀。\",\"B\":\"青年时代到练潭，小镇清冷，月色依旧，湖水萎缩，大有寻梦不遇的失落之感。\",\"C\":\"中年时代到练潭，古镇衰落，老街残破，感叹铁路公路的出现破坏了古老文明。\",\"D\":\"“我” 第一次到练潭是十分偶然的。后两次去则是 “寻梦之旅” ，寄寓颇多感慨。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"对文章首尾两段的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"首段指出月的三种存在形式，尾段回忆这三种月的状态，遥相呼应。\",\"B\":\"首段没有提及练潭，尾段处处追述练潭，首尾虚实互见，相映生辉。\",\"C\":\"首段凌空起笔，格调高品，节奏明快，浓烈的诗意和情韵溢于言表。\",\"D\":\"尾段徐徐落笔，格调低沉，节奏舒缓，浓重的忧愁和焦虑表露无遗。\"}"', 'B', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.236 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列选项中，最适合做文章标题的是（ ）"', '"{\"A\":\"练潭观月\",\"B\":\"练潭对月\",\"C\":\"练潭寻月\",\"D\":\"练潭梦月\"}"', 'D', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.240 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2020, '2020年体育单招文化考试', '"下列对文中加点字的注音，不正确的一项是（ )"', '"{\"A\":\"霰（xiàn)\",\"B\":\"壑（hè)\",\"C\":\"庇（bi)\",\"D\":\"禀（lǐn)\"}"', 'B', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.246 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2020, '2020年体育单招文化考试', '"下列对文中画线词语的解释， 不正确 的一项是（ ）"', '"{\"A\":\"扶疏：枝叶茂盛错落。\",\"B\":\"卓然：高直的样子。\",\"C\":\"交零：交替着落下来。\",\"D\":\"彰闻：广泛地传闻。\"}"', 'D', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.252 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对文中“岂徒风朝月夕为耳目之资者哉？”一句的翻译，正确的一项是（ ）"', '"{\"A\":\"难道只是在早晨晚上欣赏风景作为视听的凭借吗？\",\"B\":\"难道只有在早晨晚上才有闲暇有心情欣赏风景吗？\",\"C\":\"难道人们在早晨有风晚上有月时不能欣赏风景吗？\",\"D\":\"难道人们在有风有月之夜没有欣赏风景的凭借吗？\"}"', 'C', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.259 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对“丝桐间发，莫能拟其声”一句话的理解，正确的一项是（ ）"', '"{\"A\":\"“丝桐间发” ，指桐树发出声音； “莫能拟其声” ，指不能模仿松树的声音。\",\"B\":\"“丝桐间发”，指桐树发出声音；“莫能拟其声”，指不能比拟松树的品格。\",\"C\":\"“丝桐间发”，指琴弦发出声音；“莫能拟其声”，指不能模仿松树的声音。\",\"D\":\"“丝桐间发”，指琴弦发出声音；“莫能拟其声”，指不能表现松树的品格。\"}"', 'D', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.265 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列对本文内容和修辞手法的理解， 不正确 的一是（ ）"', '"{\"A\":\"作者将松树比作圣贤，赋予它种种美德，认为它可以启发帝王制定礼法。\",\"B\":\"作者认为参悟松树可以修身养性，使人清廉有节，正直不阿，彰明令德。\",\"C\":\"全篇多用对偶句，前呼后应，抑扬顿挫，具有整齐凝练、声韵和谐之美。\",\"D\":\"全篇多用排比句，气势充沛，感情强烈，具有节奏明快、意脉融贯之美。\"}"', 'A', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.272 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2020, '2020年体育单招文化考试', '"下列有关“岁寒堂”命意由来的判断，正确的一项是（ ）"', '"{\"A\":\"出自《庄子·让王》：“孔子曰：“天寒既至，霜雪既降，吾是以知松柏之茂也。”\",\"B\":\"出自《荀子·大略》：“岁不寒无以知松柏，事不难无以知君子。”\",\"C\":\"出自《淮南子·俶真训》：“夫大寒至，霜雪降，然后知松柏之茂也。”\",\"D\":\"出自《论语·子罕》：“子曰：“岁寒，然后知松柏之后凋也。＇”\"}"', 'A', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.277 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2021, '2021年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全部相同的一项是（ ） [cite: 716]"', '"{\"A\":\"兴致 兴奋 云兴霞蔚\",\"B\":\"范畴 酬劳 运筹帷幄\",\"C\":\"更迭 更加 改弦更张\",\"D\":\"弊端 瞥视 敝帚自珍\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"[cite_start]下列各组词语，书写完全正确的一项是（ ） [cite: 721]"', '"{\"A\":\"问鼎 逆时针 雷霆万钧\",\"B\":\"荣膺 间奏曲 戮力同心\",\"C\":\"请樱 殊不知 甚器尘上\",\"D\":\"抛锚 书券气 旁征博引\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"[cite_start]依次填入下面一段文字横线处的词语，最恰当的一项是（ ） [cite: 721]"', '"{\"A\":\"导向 转移 解决\",\"B\":\"导向 转变 解释\",\"C\":\"方向 转移 解释\",\"D\":\"方向 转变 解决\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2021, '2021年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用，不正确的一项是（ ) [cite: 722]"', '"{\"A\":\"在决赛前的恳谈会上，队员们打开天窗说亮话，总结了技战术方面的一些弱点。\",\"B\":\"赛事营销要放长线，钓大鱼，一份长期稳定的合约无疑能给双方带来更多好处。\",\"C\":\"对方谈判代表总是闪烁其词，当面锣，对面鼓，让人捉摸不透他们的真实意图。\",\"D\":\"羊毛出在羊身上，这些球星通过一些商业活动，很快赚回俱乐部高昂的转会费。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2021, '2021年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ） [cite: 726]"', '"{\"A\":\"精神追求与身体实践的兼容并蓄，蕴含着中国博大精深的传统体育文化理念。\",\"B\":\"昔日活跃于街头巷尾的传统体育项目在城市化的进程中销声匿迹，令人担忧。\",\"C\":\"在任今天的足球比赛中，他是第一个进球的始作俑者，突显出名将的迷人风采。\",\"D\":\"最终，她凭着坚定的意志和自信心，力挽狂澜，为全队夺冠立下了汗马功劳。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 731]"', '"{\"A\":\"如何运用人工智能技术发展体育事业，是每个体育工作者必须思考的大问题。\",\"B\":\"你知不知道，体育比赛不仅关乎个人，而更多的是关乎团队甚至国家民族吗？\",\"C\":\"马拉松赛的仪式除了具备一般体育赛事的组织特征，还具有自己的文化特征。\",\"D\":\"赛会的发展，明星的效应，品牌的塑造，都因体育传播产生巨大的商业价值。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]在横线处填入下列句子，顺序最恰当的一项是（ ） [cite: 733]"', '"{\"A\":\"②①③⑤④\",\"B\":\"⑤④③②①\",\"C\":\"①②③⑤④\",\"D\":\"④③⑤②①\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]下列各句中使用的修辞手法，与其他三项不同的一项是（ ） [cite: 735]"', '"{\"A\":\"罗布泊像钟摆一样一次次地位移，逐水而居地罗布人总是在搬迁。\",\"B\":\"雪水使河道很快地恢复了昔日地样子，在阳光中如一条起伏地丝带。\",\"C\":\"每天傍晚，夕阳看我的目光虽然不再锐利，但却有着期待与鼓舞。\",\"D\":\"草地变成了明镜似地湖泊，昔日地湖泊又形成了奇形怪状地石山。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2021, '2021年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ） [cite: 736]"', '"{\"A\":\"亭侧的巨石上，刻着篆书的《醉翁亭》三个大字，碑石斜卧，似呈醉态。\",\"B\":\"哦，好一座威武庄严的雄关！山海关，这号称“天下第一关”的山海关！\",\"C\":\"仰头看吧！天都峰，果然像过去的旅行家描写的那样－“卓绝云际”。\",\"D\":\"是的，如果没有了园，那还叫江南吗？我的心里，一阵孤独，一阵复杂。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"[cite_start]下列有关作家作品的表述 不正确 的一项是（ ） [cite: 736]"', '"{\"A\":\"《师说》是唐代文学家韩愈的一篇散文。\",\"B\":\"《钱塘湖春行》是北宋苏轼的一首七律诗。\",\"C\":\"《故乡》是鲁迅小说集《呐喊》中的一篇。\",\"D\":\"《飞鸟集》是印度诗人泰戈尔的代表作品。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2021, '2021年体育单招文化考试', '"下列加点字的读音， 不正确 的一项是（ ）"', '"{\"A\":\"屏住（pǐng)\",\"B\":\"小心翼翼（yì)\",\"C\":\"参半（cān)\",\"D\":\"作茧自缚（fù)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ ）"', '"{\"A\":\"蠢蠢欲动：形容蠢笨乱作一团的样子。\",\"B\":\"归宿：最终的着落。\",\"C\":\"如释重负：形容解除压力的轻松愉快。\",\"D\":\"浸渍：用液体泡着。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"对第1段中“房间里像是下起了小雨，沙沙作响”一句，理解和分析正确的一项是（ ）"', '"{\"A\":\"母亲撒桑叶的声音，像下起小雨，沙沙作响。\",\"B\":\"母亲不停地撒着桑叶，挥汗如雨，沙沙作响。\",\"C\":\"蚕贪婪地啃食着桑叶，声音急促，沙沙作响。\",\"D\":\"蚕因争食互相打斗，像下起小雨，沙沙作响。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"对第2段中“记得蚕种是论“张”的”这句话，理解和分析正确的一项是（ ）"', '"{\"A\":\"“记得”表示不能确定，“张”是表示蚕种单位的量词。\",\"B\":\"“记得”表示没有忘记，“张”是表示蚕种单位的量词。\",\"C\":\"“记得”表示不能确定，“张”是表示蚕种状态的动词。\",\"D\":\"“记得”表示没有忘记，“张”是表示蚕种状态的动词。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"对第3段中“现实越是苦闷，内心的阳光和梦想就越是强烈”这句话，理解和分析不正确的一项是（ ）"', '"{\"A\":\"现实的苦闷，强烈刺激了自尊心，让老实巴交的农民开始思考致富之路。\",\"B\":\"现实的苦闷，激发了内心的憧憬与追求，让农民坚信劳动可以改变生活。\",\"C\":\"这句话表现了那个时代农民的不甘贫苦、吃苦耐劳、愈挫愈勇的可贵精神。\",\"D\":\"这句话是作者回想、反思过去时光的结论，表现出些许的慨叹和沧桑感。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"作者在第四段谈到“束缚”和“意识”，对此的理解和分析不正确的一项是（ ）"', '"{\"A\":\"人们通常所说的“作茧自缚”其实是一种基于比喻的偏见。\",\"B\":\"“自缚”是蚕生命历程中的自然归宿，而不是它的生存困境。\",\"C\":\"“束缚”是一种有积极意义的限制，可以让人集中精力做事。\",\"D\":\"越是“勇于“自缚””的人生，就越能发挥人的潜能和价值。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"文章首尾两段都提到了“底色”一词，对其意蕴的理解和分析不正确的一项是（ )"', '"{\"A\":\"养蚕的经历，已成为“我”记忆深处挥之不去的印象。\",\"B\":\"养蚕的经历，让“我”学会看待生活和生活中的自己。\",\"C\":\"养蚕的经历，让“我”领悟到美来自劳动创造的道理。\",\"D\":\"养蚕的经历，让“我”珍惜像“茧”一样的小小家庭。\"}"', 'D', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.283 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是（ ）"', '"{\"A\":\"蚕啊蚕\",\"B\":\"茧啊茧\",\"C\":\"蚕与茧\",\"D\":\"茧与蚕\"}"', 'B', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.289 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2021, '2021年体育单招文化考试', '"下列对文中加点字的注音， 不正确 的一项是（ ）"', '"{\"A\":\"处（chǔ)\",\"B\":\"夷（yí)\",\"C\":\"桓（héng)\",\"D\":\"说（yuè)\"}"', 'D', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.296 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2021, '2021年体育单招文化考试', '"下列对文中画线词语的解释，不正确一项（ ）"', '"{\"A\":\"得意：称心如意。\",\"B\":\"诈胜：突然取胜。\",\"C\":\"太牢：牛羊猪齐备。\",\"D\":\"独擅：独自做主。\"}"', 'C', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.301 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列对文中“之”字用法的解释，正确的一项是（ ）"', '"{\"A\":\"“天下之至言”与“诈胜之徒”，“之”字用法相同。\",\"B\":\"“天下之至言”与“大胜之”，“之”字用法相同。\",\"C\":\"“大胜之”与“二子之言”，“之”字用法相同。\",\"D\":\"“大胜之”与“胜之徒”，“之”字用法相同。\"}"', 'A', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.306 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列对文中画线句子的翻译，正确的一项是（ ）"', '"{\"A\":\"谁知道我路过，却不向我请安，按罪刑罚。\",\"B\":\"谁知道我的错误，却不告诉我，按罪刑罚。\",\"C\":\"谁知道我的错误，却为我隐瞒，按罪刑罚。\",\"D\":\"谁知道我路过，却不向我汇报，按罪刑罚。\"}"', 'A', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.312 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列对文中有关内容的理解，正确的一项是（ ）"', '"{\"A\":\"“南面而立”，有“称王”的意思。\",\"B\":\"“西面而立”，有“称霸”的意思。\",\"C\":\"“东面而立”，有“称臣”的意思。\",\"D\":\"“南面” “西面” “东面” 皆无寓意。\"}"', 'C', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.318 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2021, '2021年体育单招文化考试', '"下列有关这篇短文体裁和主旨的阐释，正确的一项时（ ）"', '"{\"A\":\"这是一则史传，讲述了春秋时代三个君王的故事\",\"B\":\"这是一则政论，论证了“明主者有三惧”的观点。\",\"C\":\"这是一则颂赞，歌颂了春秋时代三个君王的美德。\",\"D\":\"这是一则寓言，说明了君王“居安思危”的道理。\"}"', 'D', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.323 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2022, '2022年体育单招文化考试', '"[cite_start]下列各组词语中加点的字，读音全都相同的一项是（ ） [cite: 757]"', '"{\"A\":\"哈欠 哈达 点头哈腰\",\"B\":\"花苞 袍子 明碉暗堡\",\"C\":\"好转 好歹 好逸恶劳\",\"D\":\"巅峰 掂量 黑白颠倒\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2022, '2022年体育单招文化考试', '"[cite_start]下列各组词语中，书写完全正确的一项是（ ） [cite: 762]"', '"{\"A\":\"编纂 闷葫芦 世外桃园\",\"B\":\"辐射 微循环 偃旗息鼓\",\"C\":\"表彰 峰窝煤 雷厉风行\",\"D\":\"临摹 绊脚石 灸手可热\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2022, '2022年体育单招文化考试', '"[cite_start]依次填人下面一段文字横线处的词语，最恰当的一项是（ ） [cite: 767]"', '"{\"A\":\"创造 提升 促进\",\"B\":\"打造 提升 促进\",\"C\":\"打造 提高 促成\",\"D\":\"创造 提高 促成\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2022, '2022年体育单招文化考试', '"[cite_start]下列各句中加点熟语的使用，不正确的一项是（ ） [cite: 773]"', '"{\"A\":\"这是我每天回家的路，太熟悉了，闭着眼睛捉麻雀，天再黑也不会走错。\",\"B\":\"冰冻三尺非一日之寒，看这姿势就知道他练习滑雪已不是一天两天了。\",\"C\":\"妈妈回来一看，妹妹正坐在地上嚎啕大哭，不分青红皂白就把我说了一顿。\",\"D\":\"我们三个人呢，怎么可能辩不过他一个?你不要长他人志气灭自家威风!\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2022, '2022年体育单招文化考试', '"[cite_start]下列各句中加点成语的使用，不正确的一项是（ ） [cite: 778]"', '"{\"A\":\"冰墩墩在冬奥会上可谓名副其实的“社交利器”，获得全世界的关注和喜爱。\",\"B\":\"北京冬残奧会即将开幕，中国体育代表团近百名运动员厉兵秣马，整装待发。\",\"C\":\"“开学第一课”上，同学们观看了女足亚洲杯夺冠的视频片段，纷纷拍手称快。\",\"D\":\"最近这部热播剧的片尾音乐如泣如诉，温情而深沉，听得人禁不住热泪盈眶。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]下列句子中，有语病的一项是（ ） [cite: 783]"', '"{\"A\":\"冬奥原创交响组曲《冰雪相约》，构思巧妙，气势磅礴，听起来令人心潮澎湃。\",\"B\":\"这部电影展示了不少既精彩又惊险的滑雪场面，比如U型池比赛、障碍追逐等。\",\"C\":\"奥林匹克运动诞生伊始，就承载着人类对“和平、团结、进步”的美好追求。\",\"D\":\"奥运场馆中那些以奉献成就精彩的志愿者，成为普通民众参与奥运的重要形式。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]在横线处填入下列句子或短语，顺序最恰当的一项是（ ） [cite: 788]"', '"{\"A\":\"②①③⑤④\",\"B\":\"⑤④③②①\",\"C\":\"①②③⑤④\",\"D\":\"④③⑤②①\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]下列各句中使用的修辞手法，与其他三项不同的一项是（ ） [cite: 799]"', '"{\"A\":\"那点儿薄薄的雪在阳光下突然害了羞，微微露出些粉色\",\"B\":\"船头飞溅的浪花，和划船的小伙儿一起唱着欢乐的歌曲\",\"C\":\"桃树，杏树，梨树，你不让我，我不让你，都开满了花赶趟\",\"D\":\"蜿蜒流淌的小河如一条碧绿的丝带，环绕着美丽的村庄\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2022, '2022年体育单招文化考试', '"[cite_start]下列句子中标点符号的使用，不正确的一项是（ ） [cite: 804]"', '"{\"A\":\"“哥们儿，你到底去不去?”他又说，“你再拿不定主意我可要先走了。”\",\"B\":\"今天我主要讲两个问题一即晚自习的纪律管理和假期安全教育问题。\",\"C\":\"《离骚》《天问》和《九歌》是我国伟大的浪漫主义诗人屈原的代表作品。\",\"D\":\"在以后的岁月里，我经常会想起那个瘦弱的少年，想起他那精彩的演出。\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"[cite_start]下列有关作家作品的表述，不正确的一项是（ ） [cite: 809]"', '"{\"A\":\"姚鼐的《登泰山记》是古代写景抒情的名篇\",\"B\":\"《荷塘月色》和《背影》都是朱自清的作品\",\"C\":\"《拿来主义》选自鲁迅的散文集《朝花夕拾》\",\"D\":\"李清照的《声声慢》用很多叠字表达感情\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2022, '2022年体育单招文化考试', '"词语中加点字的读音，不正确的一项是（ ）"', '"{\"B\":\"波澜（lán）\",\"C\":\"悄然（qiāo）\",\"D\":\"梭子（suō）\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2022, '2022年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ ）"', '"{\"A\":\"凝神屏气：指小心翼翼，极度紧张\",\"B\":\"混混浊浊：模模糊糊，丝毫都不清晰\",\"C\":\"从容不迫：非常镇静、不慌不忙的样子\",\"D\":\"有条不紊：有条理，有次序，一点儿都不乱\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"根据文意，对“纺车声”总结不正确的一项是（ ）"', '"{\"A\":\"坚韧\",\"B\":\"固执\",\"C\":\"永恒\",\"D\":\"独特\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"对文中“果子一样成熟”理解不正确的一项是（ ）"', '"{\"A\":\"将线锤比喻成“果子”，饱含了作者的喜爱。\",\"B\":\"外祖母机械地纺出一个个线锤。\",\"C\":\"作者期待着线锤一点点成型、完美。\",\"D\":\"作者赞美外祖母的辛勤纺线。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"对文中的“世间琐事”的理解不正确的是（ ）"', '"{\"A\":\"也能成为世间大事\",\"B\":\"经纬天下的人也应该重视\",\"D\":\"从古至今，无穷无尽\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"作者认为，一个好的工厂企业应该具备的素质是（ ）"', '"{\"B\":\"做小事情\",\"C\":\"做大事情\",\"D\":\"衣被苍生\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"根据内容分析，与文章内容不相符的一项是（ ）"', '"{\"A\":\"文章从外祖母早年纺线写起，写出了整个时代的辛酸。\",\"B\":\"文章写妈妈和婶子经线，怀旧之中有一种温暖的自豪。\",\"C\":\"文章描写织布中的梭子往来，表达了小事情中也可以有大境界的思想。\",\"D\":\"文章在新衣新年以及布衣难得的感慨中，流露出传统不再的复杂情感。\"}"', 'A', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.331 +00:00');
