INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2022, '2022年体育单招文化考试', '"下列对文中加点字的注音，不正确的一项是（ ）"', '"{\"A\":\"薄于身而厚于民(bó）\",\"B\":\"不以养嗜欲(shì）\",\"C\":\"不肖者处下而不怨(xiāo）\",\"D\":\"故家货殚< dān）\"}"', 'D', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.341 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"不列对句中加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"约于身而广于世 约：约束\",\"B\":\"赏不遗贱 遗：遗漏\",\"C\":\"刑罚中于法 中：合乎\",\"D\":\"故民心离 故：故意\"}"', 'A', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.347 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"对下列句子中加点词的解说，不正确的一项是（ ）"', '"{\"A\":\"“足以明政行教”， “以”是介词，表示凭借。\",\"B\":\"“废罪顺于民”， “于”是介词，引进“顺”的对象。\",\"C\":\"“是以贤者处上而不华”， “而”是连词，表示意思的递进。\",\"D\":\"“粒食之民”， “之”放在定语和中心语之间，相当于助词“的”。\"}"', 'B', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.354 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"“为政尚相利，故下不以相害”这个复句的类型是（ ）"', '"{\"A\":\"选择复句\",\"B\":\"转折复句\",\"C\":\"递进复句\",\"D\":\"因果复句\"}"', 'D', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.359 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"下列对文中画线句子的翻译，正确的一项是（ ）"', '"{\"A\":\"竭尽智慧引导民众，但不会在这事上夸耀。\",\"B\":\"尽量用智慧引导民众，而不是去讨伐他们。\",\"C\":\"竭尽智慧引导民众，也就不会去讨伐他们。\",\"D\":\"尽量用智慧引导民众，但不会去借此夸耀。\"}"', 'A', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.365 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2022, '2022年体育单招文化考试', '"下列对文中有关内容的理解，不正确的一项是（ ）"', '"{\"A\":\"景公向晏子请教古代盛君的问题，是想知道自己怎么做才比较好。\",\"B\":\"晏子从多个角度告诉景公古代盛君的特点，希望能够影响到景公。\",\"C\":\"景公听了晏子的回答之后，显然心里明白可是他没有做任何反应。\",\"D\":\"晏子不得已之下只好直接指出了国君的问题，景公这才明白过来。\"}"', 'A', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.370 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2023, '2023年体育单招文化考试', '"下列各组词语中加点的字，读音全都相同的一项是（ ）"', '"{\"A\":\"重要 重量 山重水复\",\"B\":\"木拴 诠释 病情痊愈\",\"C\":\"应酬 应对 得心应手\",\"D\":\"谱写 铺垫 普天同庆\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2023, '2023年体育单招文化考试', '"下列各组词语，没有错别字的一项是（ ）"', '"{\"A\":\"斟酌 泼水节 意气风发\",\"B\":\"校对 护身符 大意凛然\",\"C\":\"遵从 微波炉 心无旁务\",\"D\":\"模擦 金雏棒 乱七八糟\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2023, '2023年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"突破 确立 占据\",\"B\":\"冲破 树立 占领\",\"C\":\"冲破 确立 占领\",\"D\":\"突破 树立 占据\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2023, '2023年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"单位给他涨工资了，他却洋洋得意地说了些漂亮话，真是得了便宜还卖乘!\",\"B\":\"有那么一些人做起事情来总是前怕狼,后怕虎，顾虑太多，结果一事无成。\",\"C\":\"比赛中出现的失误反而让观众记住了他的名字，他这是捡了芝麻，丢了西瓜。\",\"D\":\"不入虎穴，焉得虎子?要想取得成功，就要有敢闯敢拼、不怕困难的奋斗精神。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2023, '2023年体育单招文化考试', '"下列各句中加点成语的使用，不正确的一项是（ ）"', '"{\"A\":\"北京冬奥会成功举办后不到一年的时间里，冰雪运动在全国各地已经蔚然成风。\",\"B\":\"在现场观众捶胸顿足的背后，不知凝聚了运动员们多少个日日夜夜的辛勤付出。\",\"C\":\"正在举行的全国两会上，委员们就如何加快建设体育强国各抒己见，献计献策。\",\"D\":\"参加此次世界杯的五位中国选手在首轮比赛中都旗开得胜，顺利晋级第二阶段。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列句子中，有语病的一项是（ ）"', '"{\"A\":\"随着春节假期的逐渐临近，全球各地旅游相关行业都在热切期待着中国游客的到来。\",\"B\":\"运动健身不仅是身体免疫力和生活质量的重要途径，也是建设健康中国的必然要求。\",\"C\":\"龙舟、围棋和太极拳等中华传统体育项目已走出国门，向世界展示中华文化的智慧。\",\"D\":\"现在，人们对运动的个性化需求不断增加，像马术这类小众项目也越来越受到行睐。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ ）"', '"{\"A\":\"⑤③④②①\",\"B\":\"①④⑤③②\",\"C\":\"②④①⑤③\",\"D\":\"④①⑤③②\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列各句中，没有使用夸张手法的一项是（ ）"', '"{\"A\":\"白发三千丈，缘愁似个长。\",\"B\":\"三杯两盏淡酒，怎敌他、晚来风急!\",\"C\":\"飞流直下三千尺，疑是银河落九天。\",\"D\":\"山，快马加鞭未下鞍。惊回首，离天三尺三。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2023, '2023年体育单招文化考试', '"下列句子中标点符号的使用不正确的一项是（ ）"', '"{\"A\":\"“鸟语花香”,“桃李争艳”,“”草长莺飞，这三个词语都是用来描绘春天的。\",\"B\":\"什么事情该做，什么事情不该做，都需要做父母的时刻来提醒孩子。\",\"C\":\"社会习惯的改变——这常常是社会变革带来的结果，会对语言产生影响。\",\"D\":\"我独自站在那喃喃自语：“我怎么欺骗他了?我伤害他了?还是我侮辱他了?”\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是（ ）"', '"{\"A\":\"“先天下之忧而忧”出自欧阳修的《醉翁亭记》。\",\"B\":\"闻丁多的代表作包括，《红烛》《死水》等。\",\"C\":\"“豆腐西施”杨二嫂是鲁迅小说《故乡》中的人物。\",\"D\":\"杜甫名作《登高》营造了沉郁悲凉的意境。\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2023, '2023年体育单招文化考试', '"对下列词语中加点字的注音，不正确的一项是 （ ）"', '"{\"A\":\"惆怅(chàng)\",\"B\":\"虔敬(qián)\",\"C\":\"沮丧(sāng)\",\"D\":\"阴霾(mái)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2023, '2023年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是 （ ）"', '"{\"A\":\"枝叶繁茂：枝和叶长得很密很盛。\",\"B\":\"萎靡：形容意志消沉的样子。\",\"C\":\"参差有致：高低不一，错落有致。\",\"D\":\"亮丽：形容色彩明亮的样子。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"对文中野樱桃移植和养护场景的理解和分析，不正确的一项是 （ ）"', '"{\"A\":\"隐含了对外祖父的怀念。\",\"B\":\"表现了对故乡植物的珍视。\",\"C\":\"表现了难离故土的情感。\",\"D\":\"为下文妈妈的去世做了铺垫。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"对文中“妈妈、爸爸，这是女儿所能给你们的最好的礼物了”的理解，不正确的一项是 （ ）"', '"{\"A\":\"表明妈妈和爸爸多年来对故乡十分牵挂。\",\"B\":\"表明女儿对妈妈、爸爸的心意非常了解。\",\"C\":\"表明女儿的故乡之行其实是代父母去的。\",\"D\":\"表明子女对父母精神生活的关注很重要。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列各句中，使用比喻修辞的一项是 （ ）"', '"{\"A\":\"好奇与激动的程度不亚于太空人登月球。\",\"B\":\"我幻想着他们接过了泥土的小胶袋，也同时接过一大串属于他们的美好年月。\",\"C\":\"盆里的两株小兰芽，泛着比星星还耀眼的光芒。\",\"D\":\"几片叶子立得笔直，如同最绿的翡翠。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"“照例是个黯淡的黄昏，照例地拿着铜质小水壶，给花窗上的几盆花草做三天一次的浇水。”对句中两处“照例”的理解和分析，不正确的一项是 （ ）"', '"{\"A\":\"通过词语的意义不经意间演绎了时间的流淌。\",\"B\":\"表明作者的生活很刻板，没有什么开心的事。\",\"C\":\"说明作者这样的生活节奏已持续了一段时间。\",\"D\":\"与下文发现新芽的惊喜形成反差的艺术效果。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列理解和分析，与原文意思不相符的一项是 （ ）"', '"{\"A\":\"文章首段称故乡之行为“人生旅途上的高潮”,饱含着作者对故乡的情感。\",\"B\":\"文中“我”和`家人都叫不知名的兰花为“中国兰”,寄托了海外游子的情思。\",\"C\":\"作者一度认为“故乡的泥土是死土”,表明作者的情绪受到周围事物的影响。\",\"D\":\"重生的中国兰带有一股浓郁扑鼻的芳香，由此转变了作者对故乡泥土的看法。\"}"', 'A', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.376 +00:00');
