INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2023, '2023年体育单招文化考试', '"下列对文中加点字的注音，不正确的一项是 （ ）"', '"{\"A\":\"昔者晋献公欲假道于虞以伐虢(jia)\",\"B\":\"赂虞公(lu)\",\"C\":\"马犹取之内原而著之外既也(zhù)\",\"D\":\"还反处三年(huán)\"}"', 'C', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.388 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列对文中加点词的解释，不正确的一项是 （ ）"', '"{\"A\":\"求假道焉 求：请求。\",\"B\":\"君勿忧 忧:担心。\",\"C\":\"荀息伐虢克之 克：攻克\",\"D\":\"献公说曰 说；说话\"}"', 'D', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.395 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"对下列句子中“之”字的解说，不正确的一项 （ ）"', '"{\"A\":\"吾先之君宝也，“之”放在定语与名词之间，略等于现代汉语的“的”\",\"B\":\"虞公贪利其璧与马而欲许之，“之”代指前文的“璧与马”\",\"C\":\"夫虞之有虢也，如车之有辅，“之”放在主语谓语之间，取消句子独立性\",\"D\":\"荀息伐虢克之，“之”略等于现代的他\"}"', 'A', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.401 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"“若假之道，则虢朝亡而虞夕从之矣”。这个复句的类型是 （ ）"', '"{\"A\":\"选择复句\",\"B\":\"转折复句\",\"C\":\"假设复句\",\"D\":\"因果复句\"}"', 'D', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.406 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列对文中画线句子的翻译，正确的一项是 （ ）"', '"{\"A\":\"璧还和以前一样。即便如此，马还是长儿岁了。\",\"B\":\"璧还和以前一样。即便如此，但马的牙齿长好了。\",\"C\":\"虽然马的牙齿更加长了，但璧还是和以前一样。\",\"D\":\"璧还和以前一样，但马的牙齿和从前不一样了。\"}"', 'C', '这是一道生成的练习题', '3', 23, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.411 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2023, '2023年体育单招文化考试', '"下列对文中有关内容的理解，不正确的一项是 （ ）"', '"{\"A\":\"晋献公对于自己的璧与马十分喜爱，担心虞公收了礼物却不借道\",\"B\":\"荀息认为把宝物送给虞公，只是换了个地方收藏，迟早会拿回来\",\"C\":\"虞公不听宫之奇的劝谏，贪图晋国的璧与马，最终导致国家灭亡\",\"D\":\"荀息攻克了虢国后，顺道攻克虞国，夺回了璧与马，献公很高兴\"}"', 'C', '这是一道生成的练习题', '3', 24, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.418 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2024, '2024年体育单招文化考试', '"下列各组词语中加点的字，读音全部相同的一项是（ ）"', '"{\"A\":\"宿舍 舍妹 退避三舍\",\"B\":\"篓子 槛楼 屡教不改\",\"C\":\"依畏 猥琐 泾渭分明\",\"D\":\"会客 会计 心领神会\"}"', 'A', '', '3', 1, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2024, '2024年体育单招文化考试', '"下列各组词语，书写完全正确的一项是（ ）"', '"{\"A\":\"示意 拦路虎 自爆自弃\",\"B\":\"玉佩 赶时髦 筹踞满志\",\"C\":\"编缉 凌分子 妙趣横生\",\"D\":\"果腹 金刚钻 相濡以沫\"}"', 'B', '', '3', 2, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2024, '2024年体育单招文化考试', '"依次填入下面一段文字横线处的词语，最恰当的一项是（ ）"', '"{\"A\":\"抵御 特定 损害\",\"B\":\"抵御 特制 损伤\",\"C\":\"抵抗 特制 损害\",\"D\":\"抵抗 特定 损伤\"}"', 'C', '', '3', 3, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2024, '2024年体育单招文化考试', '"下列各句中加点熟语的使用，不正确的一项是（ ）"', '"{\"A\":\"小李新买了一个飞船模型，兴冲冲地拿去找同桌一起玩，没想到碰了一鼻子灰。\",\"B\":\"产品设计如果一味追求标准化和模式化，不敢越雷池一步，势必丧失创新活力。\",\"C\":\"面试官问了几个挺简单的专业问题，不料他却支支吾吾，回答得驴唇不对马嘴。\",\"D\":\"今天，美术馆里刚展出了一幅人物肖像画，面像里的人物惟妙惟肖，有鼻子有限。\"}"', 'D', '', '3', 4, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2024, '2024年体育单招文化考试', '"下列各句中加点成语的使用，不正确的一项是（ ）"', '"{\"A\":\"昨天学校的游泳比赛上，他又一次当仁不让地拿到了冠军。\",\"B\":\"姐姐当时没说什么，分别后写信来劝他，句句是金玉良言。\",\"C\":\"东方艺术大楼的飞檐、斗拱、琉璃瓦古色古香，极具韵味。\",\"D\":\"听说家中变故，他五内俱焚，急忙乘坐最早的航班赶回去。\"}"', 'A', '', '3', 5, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列句子中，有语病的一项是（ ）"', '"{\"A\":\"坚持体育锻炼有助于保持心理健康，降低患神经退行性疾病的风险。\",\"B\":\"针对老人、小孩及生活不能自理者，使用本产品时应有成年人看护。\",\"C\":\"在较粗组织损伤的慢性期，热敷有一定地缓解疼痛，促进恢复的效果。\",\"D\":\"以不确定、叠加、纠缠等为特征的量子是一种崭新的思维方式。\"}"', 'B', '', '3', 6, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"在横线处填入下列句子或短语，顺序最恰当的一项是（ ）"', '"{\"A\":\"①②③④\",\"B\":\"①③②④\",\"C\":\"④①③②\",\"D\":\"③④①②\"}"', 'C', '', '3', 7, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列各句中使用的修辞手法，与其他三项不同的一项是（ ）"', '"{\"A\":\"他在房间默默出神的时候，一道青白的电光猛地撕裂了漆黑的夜空。\",\"B\":\"窗台上的水仙花清丽脱俗，如同身着白衣裙、亭亭玉立的河边少女。\",\"C\":\"我打了个寒颤，我就知道，我们之间已经隔了一层可悲的屏障壁了。\",\"D\":\"极光有时出现时间极短，如节日的焰火在空中闪现一下又马上消失。\"}"', 'D', '', '3', 8, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('grammar', 2024, '2024年体育单招文化考试', '"下列句子中标点符号的使用，不正确的一项是（ ）"', '"{\"A\":\"北京紫禁城一共有四座城门：午门、神武门、东华门和西华门。\",\"B\":\"课堂上，小明问：“老师，‘有条不紊’的‘素’是什么意思？”\",\"C\":\"以《时间的故事》为主题的数字互动体验展终于落户北京鼓楼。\",\"D\":\"同伴邀我一起去，我心情不好，淡淡地说：“算了，你们去吧。”\"}"', 'A', '', '3', 9, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列有关作家作品的表述，不正确的一项是（ ）"', '"{\"A\":\"繁琐和鲁莽都是曹禺的活剧《雷雨》中的人物。\",\"B\":\"《春江花月夜》是唐代诗人张若虚对月抒情的名篇。\",\"C\":\"《繁星》《春水》和《新月集》都是冰心所著诗集。\",\"D\":\"韩愈在散文《师说》中慨叹“师道之不传也久矣！”\"}"', 'B', '', '3', 10, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2024, '2024年体育单招文化考试', '"下列词语中加点字的读音，不正确的一项是（ ）"', '"{\"A\":\"余脉 (mài)\",\"B\":\"宽绰 (chuò)\",\"C\":\"踌躇 (màn)\",\"D\":\"村寨 (zhài)\"}"', 'C', '', '3', 11, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('vocabulary', 2024, '2024年体育单招文化考试', '"对下列词语在文中意思的解释，不正确的一项是（ ）"', '"{\"A\":\"横竖：指把一物体嵌入另一物体内\",\"B\":\"临歧：指来到从大路分出的岔路。\",\"C\":\"奇妙：指难得的货物。\",\"D\":\"须臾：指较长的时间。\"}"', 'D', '', '3', 12, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对第2段引用苏东坡《岐亭五首（并叙）》中诗句的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"照应前文所提岐亭的位置及几幅山的环境。\",\"B\":\"引出下文中对陈季常相符苏东坡吃饭的铺叙。\",\"C\":\"再现了苏东坡与陈季常之间长久而深厚的友情。\",\"D\":\"体现出与写作“无为在歧路，儿女共沾巾”的主物的不同人生追求。\"}"', 'A', '', '3', 13, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对“哪有我们这般故人相逢灯火可亲有情味”句中“情味”的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"在风雪天里，出汗穴，一路风坐仆仆，终于遇见故人相逢，感到一种安慰。\",\"B\":\"在朋友家中受到热情接待，特别新鲜丰盛的酒菜，让人产生大快朵颐的满足。\",\"C\":\"陈季常不同于其父，待人亲切平等，让苏东坡又父子二人为人上的深刻差别。\",\"D\":\"二十年后重逢，无论成仕成隐，彼此都入到中年，对人生有了更多沧桑感慨。\"}"', 'B', '', '3', 14, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列各句中，使用了比喻修辞的一项是（ ）"', '"{\"A\":\"大别山的余脉几幅山河完全呈现出来，果然是像九颗大小不一的螺蛳壳。\",\"B\":\"那是多么好的一餐饭，足以慰风雪，慰风尘。\",\"C\":\"苏东坡回忆起他在城北见到陈季常时，四公子骑马射箭的英姿。\",\"D\":\"《方山子传》里却说是“适见焉”“亦要然”，好像两人眼面相逢，彼此大吃一惊。\"}"', 'C', '', '3', 15, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对文中有关语句的理解和分析，不正确的一项是（ ）"', '"{\"A\":\"指出岐亭在“往西省”和“往东南”各通往哪里，证明岐亭得名是有道理的。\",\"B\":\"“骑马出汗穴，而周口，而满川”，既标示行进路线，也表明一路上心情是轻快的。\",\"C\":\"根据“季常兄项君‘高帽子’去捉鸭鹅，有助于表现脸上陈季常的性情和真诚。”\",\"D\":\"“话题呢，说不完没不完”，通过重复，表现了两人与别人重逢后追切的交流愿望。\"}"', 'D', '', '3', 16, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:03:13.921 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列理解和分析，与原文意思不相符的一项是（ ）"', '"{\"A\":\"文章详细描述岐亭地理位置，罗列周边山谷和路线，是因为岐亭在古代就位量显赫。\",\"B\":\"作者在描述该考察的较时，用了设问和反问句式，代入了对苏东坡诗作的理解。\",\"C\":\"作者认为，《岐亭五首（并叙）》中苏东坡表现出对早年才子时期心态的超越。\",\"D\":\"文章引用《方山子传》中的材料，认为“岐亭”还是“岐亭”的问题很难有一个定论。\"}"', 'D', '这是一道生成的练习题', '3', 17, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.423 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列选项中，最适合做文章标题的一项是（ ）"', '"{\"A\":\"岐亭记\",\"B\":\"儿女共沾巾\",\"C\":\"中年\",\"D\":\"友人陈季常\"}"', 'A', '这是一道生成的练习题', '3', 18, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.428 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('pinyin', 2024, '2024年体育单招文化考试', '"下列对文中加点字的注音，不正确的一项是（ ）"', '"{\"A\":\"重耳无粮，寝不能行 (nēi)\",\"B\":\"子推割股肉以食重耳 (shī)\",\"C\":\"使骖乘于国中 (cān)\",\"D\":\"然君诚故之罪，与骖乘 (shè)\"}"', 'D', '这是一道生成的练习题', '3', 19, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.434 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对于列加点词的解释，不正确的一项是（ ）"', '"{\"A\":\"晋文公重耳亡，亡：逃亡\",\"B\":\"天下莫不闻 莫：没有谁\",\"C\":\"于是里兔须造见，到去\",\"D\":\"臣之为贼亦大矣，小偷\"}"', 'A', '这是一道生成的练习题', '3', 20, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.440 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"对于列各组句子中加点词的解说，不正确的一项是（ ）"', '"{\"A\":\"“子推割股肉以食重耳”，“以”是造词，表示目的。\",\"B\":\"“君沐邪”，“邪”是造词，表示疑问。\",\"C\":\"“然君诚故之罪，与骖乘”，“然”是造词，表示转折。\",\"D\":\"“夫里兔须且不诛而骖乘”，“且”是造词，表示并列。\"}"', 'C', '这是一道生成的练习题', '3', 21, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.445 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('comprehension', 2024, '2024年体育单招文化考试', '"下列对文中画法误读部分的断句，正确的一项是（ ）"', '"{\"A\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐何/言之性也\",\"B\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐何/言之性也\",\"C\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐何/言之性也\",\"D\":\"臣闻/沐者其心倒/心俱者其言性令君/不沐/何言之性也\"}"', 'C', '这是一道生成的练习题', '3', 22, '[]', '2025-12-08 08:03:13.921 +00:00', '2025-12-08 08:45:32.453 +00:00');
