INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2025, '成语练习', '"填入下列句子中的成语，最恰当的一项是"', '"{\"A\":\"差强人意：形容大体上还能使人满意\",\"B\":\"首当其冲：表示最先受到攻击或遭遇灾难\",\"C\":\"源远流长：比喻才能全部显示出来\",\"D\":\"脱颖而出：指互相配合、补充，更能显出好处\"}"', 'B', '首当其冲的正确解释是：表示最先受到攻击或遭遇灾难', '中等', 18, '', '2025-12-08 08:33:32.778 +00:00', '2025-12-08 08:33:32.778 +00:00');
