INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('idiom', 2025, '成语练习', '"填入下列句子中的成语，最恰当的一项是"', '"{\"A\":\"脱颖而出：形容大体上还能使人满意\",\"B\":\"首当其冲：表示最先受到攻击或遭遇灾难\",\"C\":\"日新月异：比喻才能全部显示出来\",\"D\":\"差强人意：指互相配合、补充，更能显出好处\"}"', 'A', '脱颖而出的正确解释是：形容大体上还能使人满意', '中等', 20, '', '2025-12-08 08:33:32.791 +00:00', '2025-12-08 08:33:32.791 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列各组词语，没有错别字的一项是（ ）"', '"{\"A\":\"涣然一新  病入膏盲  旁征博引  再接再励\",\"B\":\"焕然一新  病入膏肓  旁征博引  再接再厉\",\"C\":\"涣然一新  病入膏肓  旁证博引  再接再励\",\"D\":\"焕然一新  病入膏盲  旁征博引  再接再厉\"}"', 'B', ''焕然一新'的'焕'是火字旁；'病入膏肓'的'肓'是月字旁底；'再接再厉'的'厉'是严厉的厉。', 'medium', NULL, '', '2025-12-08 09:10:34.520 +00:00', '2025-12-08 09:10:34.520 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列句子中没有错别字的一项是（ ）"', '"{\"A\":\"他这个人做事总是马马虎虎，经常丢三落四的。\",\"B\":\"这篇文章的论点明确，论据充足，很有说服力。\",\"C\":\"我们必须刻服困难，才能取得更大的进步。\",\"D\":\"他的歌声优美动听，令人心旷神怡。\"}"', 'B', ''刻服'应为'克服'。', 'medium', NULL, '', '2025-12-08 09:10:34.535 +00:00', '2025-12-08 09:10:34.535 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列成语书写正确的一项是（ ）"', '"{\"A\":\"察言观色  骇人听闻  名列前矛  按部就班\",\"B\":\"察言观色  赅人听闻  名列前茅  按部就班\",\"C\":\"察言观色  骇人听闻  名列前茅  按步就班\",\"D\":\"察言观色  骇人听闻  名列前茅  按部就班\"}"', 'D', ''骇人听闻'的'骇'是骇客的骇；'名列前茅'的'茅'是茅草的茅；'按部就班'的'部'是部门的部。', 'medium', NULL, '', '2025-12-08 09:10:34.541 +00:00', '2025-12-08 09:10:34.541 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列各组词语中，有错别字的一组是（ ）"', '"{\"A\":\"沧桑  雕琢  蜿蜒  辍学\",\"B\":\"干涸  惊骇  瑕疵  竣工\",\"C\":\"吝啬  阔绰  偏僻  作祟\",\"D\":\"呻吟  深遂  闪烁  瞻仰\"}"', 'D', ''深遂'应为'深邃'，邃是邃远的邃。', 'medium', NULL, '', '2025-12-08 09:10:34.545 +00:00', '2025-12-08 09:10:34.545 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列句子中错别字修改正确的一项是（ ）\n原句：在老师的指导下，我们终于克苦学习了。"', '"{\"A\":\"克苦→刻苦\",\"B\":\"指道→指导\",\"C\":\"导下→导下\",\"D\":\"学习→学西\"}"', 'A', ''克苦'应为'刻苦'，意思是忍受艰苦，努力奋斗。', 'medium', NULL, '', '2025-12-08 09:10:34.549 +00:00', '2025-12-08 09:10:34.549 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列词语中没有错别字的一项是（ ）"', '"{\"A\":\"布置  部署  必须  必需\",\"B\":\"布置  部暑  必须  必需\",\"C\":\"布置  部署  必需  必须\",\"D\":\"布署  部署  必须  必需\"}"', 'A', ''部署'的'部'不能写成'布'；'必须'表示一定要，'必需'表示不可缺少。', 'medium', NULL, '', '2025-12-08 09:10:34.555 +00:00', '2025-12-08 09:10:34.555 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列句子中有错别字的一项是（ ）"', '"{\"A\":\"这篇文章的寓意深刻，发人深省。\",\"B\":\"我们要珍惜来之不易的幸福生活。\",\"C\":\"他做事一向一丝不苟，从不马虎。\",\"D\":\"经过努力，他的成绩有了显注的提高。\"}"', 'D', ''显注'应为'显著'。', 'medium', NULL, '', '2025-12-08 09:10:34.560 +00:00', '2025-12-08 09:10:34.560 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列字形完全正确的一项是（ ）"', '"{\"A\":\"叠翠  沉湎  幅射  阴霾\",\"B\":\"蝶翠  沉缅  辐射  阴霾\",\"C\":\"叠翠  沉湎  辐射  阴霾\",\"D\":\"叠翠  沉湎  幅射  阴蔂\"}"', 'C', ''辐射'的'辐'是车字旁；'沉湎'的'湎'是三点水旁。', 'medium', NULL, '', '2025-12-08 09:10:34.564 +00:00', '2025-12-08 09:10:34.564 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列各组词语中，没有错别字的一组是（ ）"', '"{\"A\":\"川流不息  名副其实  甜言密语  惩前毖后\",\"B\":\"川流不息  名付其实  甜言蜜语  惩前毖后\",\"C\":\"川流不息  名副其实  甜言蜜语  惩前毖后\",\"D\":\"穿流不息  名副其实  甜言蜜语  惩前毙后\"}"', 'C', ''名副其实'的'副'是相称的意思；'甜言蜜语'的'蜜'是蜂蜜的蜜。', 'medium', NULL, '', '2025-12-08 09:10:34.567 +00:00', '2025-12-08 09:10:34.567 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列句子中错别字最多的一句是（ ）\n小明既使生病了，也要坚持上学，他这种决不放弃的精神值的我们学习。"', '"{\"A\":\"1个\",\"B\":\"2个\",\"C\":\"3个\",\"D\":\"4个\"}"', 'C', '有3个错别字：'既使'应为'即使'；'决不'应为'决不'（'决'正确）；'值的'应为'值得'。', 'medium', NULL, '', '2025-12-08 09:10:34.572 +00:00', '2025-12-08 09:10:34.572 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列成语书写正确的一项是（ ）"', '"{\"A\":\"一愁莫展  穿流不息  言简意该  莫中一是\",\"B\":\"一筹莫展  川流不息  言简意赅  莫衷一是\",\"C\":\"一筹莫展  川流不息  言简意该  莫衷一是\",\"D\":\"一愁莫展  川流不息  言简意赅  莫中一是\"}"', 'B', ''一筹莫展'的'筹'是筹划的筹；'川流不息'的'川'是河川的川；'言简意赅'的'赅'是完备的意思；'莫衷一是'的'衷'是内心的意思。', 'medium', NULL, '', '2025-12-08 09:10:34.575 +00:00', '2025-12-08 09:10:34.575 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列各组词语中，全对的一组是（ ）"', '"{\"A\":\"赔偿  仿拂  荒唐  芳香\",\"B\":\"赔偿  仿佛  荒唐  芳香\",\"C\":\"陪偿  仿佛  慌唐  芳香\",\"D\":\"赔偿  仿佛  慌唐  芳香\"}"', 'B', ''赔偿'的'赔'是贝字旁；'仿佛'的'佛'是佛陀的佛；'荒唐'的'荒'是荒野的荒。', 'medium', NULL, '', '2025-12-08 09:10:34.582 +00:00', '2025-12-08 09:10:34.582 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列句子中没有错别字的一项是（ ）"', '"{\"A\":\"这篇文章的情节曲折离奇，引人入胜。\",\"B\":\"我们要发扬艰苦朴素的作用。\",\"C\":\"他做事认真负责，一丝不茍。\",\"D\":\"春天的公园里，百花齐放，争齐斗艳。\"}"', 'A', ''作用'应为'作风'；'一丝不茍'应为'一丝不苟'；'争齐斗艳'应为'争奇斗艳'。', 'medium', NULL, '', '2025-12-08 09:10:34.586 +00:00', '2025-12-08 09:10:34.586 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列字形完全正确的一项是（ ）"', '"{\"A\":\"安详  暴躁  璀璨  璐牌\",\"B\":\"安祥  暴燥  璀璨  路牌\",\"C\":\"安详  暴躁  璀璨  路牌\",\"D\":\"安详  暴燥  璀灿  路牌\"}"', 'C', ''安详'的'详'是详实的详；'暴躁'的'躁'是急躁的躁；'璀璨'的'璨'是灿烂的璨。', 'medium', NULL, '', '2025-12-08 09:10:34.590 +00:00', '2025-12-08 09:10:34.590 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列各组词中，有错别字的一组是（ ）"', '"{\"A\":\"稀疏  踌躇  崔嵬  蜿蜒\",\"B\":\"梳稀  踌躇  催嵬  蜿蜒\",\"C\":\"稀疏  愁躇  崔嵬  蜿蜒\",\"D\":\"稀疏  踌躇  崔嵬  蜿蜒\"}"', 'B', ''稀疏'的'稀'是稀少的稀；'崔嵬'的'崔'是崔嵬的崔。', 'medium', NULL, '', '2025-12-08 09:10:34.594 +00:00', '2025-12-08 09:10:34.594 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列句子中修改错别字正确的一项是（ ）\n原句：他对工作一惯认真负责，从不含胡。"', '"{\"A\":\"一惯→一贯  含胡→含糊\",\"B\":\"一惯→一贯  含胡→含胡\",\"C\":\"一惯→一贯  含胡→含湖\",\"D\":\"一贯→一惯  含胡→含糊\"}"', 'A', ''一惯'应为'一贯'；'含胡'应为'含糊'。', 'medium', NULL, '', '2025-12-08 09:10:34.597 +00:00', '2025-12-08 09:10:34.597 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列词语书写完全正确的一项是（ ）"', '"{\"A\":\"伶牙利齿  脍灸人口  流连忘返  留连忘返\",\"B\":\"伶牙俐齿  脍炙人口  流连忘返  留连忘返\",\"C\":\"伶牙俐齿  脍灸人口  流连忘返  留连忘返\",\"D\":\"伶牙利齿  脍炙人口  流连忘返  留连忘返\"}"', 'B', ''伶牙俐齿'的'俐'是伶俐的俐；'脍炙人口'的'炙'是烤肉的意思。', 'medium', NULL, '', '2025-12-08 09:10:34.600 +00:00', '2025-12-08 09:10:34.600 +00:00');
INSERT INTO Questions (type, year, source, content, options, answer, explanation, difficulty, questionNumber, tags, createdAt, updatedAt) VALUES ('correction', 2024, '同步练习题库', '"下列各组词语中没有错别字的一项是（ ）"', '"{\"A\":\"仓促  枯躁  酝酿  即使\",\"B\":\"仓促  枯燥  酝酿  即使\",\"C\":\"苍促  枯燥  酿酿  既使\",\"D\":\"仓促  枯燥  酿酿  既使\"}"', 'B', ''仓促'的'仓'是仓促的仓；'枯燥'的'燥'是干燥的燥；'即使'的'即'就是的意思。', 'medium', NULL, '', '2025-12-08 09:10:34.603 +00:00', '2025-12-08 09:10:34.603 +00:00');
